INSERT INTO `tips` VALUES (36850, 'Trung Hoa là đất nước phát hiện ra tơ lụa sớm nhất trên thế giới. Mà người phát\r\nminh ra tơ lụa là vợ của Thủy tổ Hiên Viên Hoàng Đế – Luy Tổ. Bà cũng được xưng là “Nhân văn nữ tổ”.', NULL, NULL);
INSERT INTO `tips` VALUES (36851, 'Tết Đoan Ngọ ở Trung Quốc thường hay có những hoạt động như ăn bánh chưng, chèo thuyền rồng, treo lá ngải ở\r\ntrước cửa v.v... mục đích chính của những hoạt động này nhằm để tưởng nhớ nhà thơ Khuất Nguyên (屈原Qūyuán ).\r\nThật ra trong lịch sử Trung Hoa, vào ngày này không chỉ có mỗi Khuất Nguyên gieo mình xuống sông Mịch La tự\r\nvẫn, mà còn có Ngũ Tử Tư （伍子胥Wǔzixū）bị dìm chết ở sông Tiền Đường, Tào Nga （曹娥Cáoé）vì cứu cha mà nhảy sông\r\nTào Nga. Do đó ngày mùng 5 tháng 5 trong lịch sử Trung Hoa là một ngày đầy tang thương mất mát, không phải\r\nlà một ngày để hoan hô vui mừng chúc tụng. 😢😢\r\nNgoài ra Đoan Ngọ cũng là ngày không tốt trong tháng 5, người xưa thường hay đuổi tà tránh dịch vào ngày\r\nnày. Nên là chỉ mong mọi người bình an khỏe mạnh là được, như thế để đúng với tính chất ngày lễ này, người\r\nTrung Quốc hay chúc nhau', NULL, NULL);
INSERT INTO `tips` VALUES (36852, 'Trước khi bạn tra từ vựng mới trong từ điển, hãy thử đoán nghĩa của nó bằng cách nhìn gốc từ. Nếu bạn biết\r\nmột vài ngôn ngữ, bạn sẽ bắt đầu nhận ra những từ mới có chung nguồn gốc. Nghiên cứu nguồn gốc của từ mới có\r\nthể giúp bạn ghi nhớ từ vựng tốt hơn.\r\n\'社牛(shè niú)\' trình xã giao đỉnh của chóp (kiểu cực tự tin không ngại thể hiện bản\r\nthân, giao tiếp với người lạ)', NULL, NULL);
INSERT INTO `tips` VALUES (36853, 'Tứ đại hoa đán Trung Quốc gồm Hoa mẫu đơn, hoa cúc, hoa thủy tiên và hoa trà..', NULL, NULL);
INSERT INTO `tips` VALUES (36854, 'Việc lặp lại từ vựng hàng trăm lần trong suốt một ngày sẽ không hiệu quả bằng việc bạn lặp lại chúng một vài\r\nlần trong khoảng thời gian, có thể là vài ngày hoặc vài tuần (nghĩa là lặp đi lặp lại cách nhau một quãng).\r\nVí dụ: bạn hãy sử dụng từ vựng mới ngay lập tức. Sau đó, cố gắng nhớ lại nó trong một giờ. Xem lại nó một\r\nthời gian ngắn trước khi bạn đi ngủ. Sử dụng lại từ vựng ấy một ngày sau đó. Cuối cùng, hãy xem lại nó sau\r\nvài ngày.', NULL, NULL);
INSERT INTO `tips` VALUES (36855, '对不住 (duì bù zhù) cũng là xin lỗi nhé!', NULL, NULL);
INSERT INTO `tips` VALUES (36856, 'Các nguyên âm: “i”, “in”, “ing”, nếu phía trước không ghép với phụ âm, lúc viết\r\nphải thêm bán nguyên âm “y” ở trước, ví dụ:\'i\' – \'yi\', \'in\' – \'yin\', \'ing\' – \'ying\'.', NULL, NULL);
INSERT INTO `tips` VALUES (36857, '先生 (Xiānshēng) thường được dùng xưng hô đối với tầng lớp trí thức trong xã hội.', NULL, NULL);
INSERT INTO `tips` VALUES (36858, 'A 给 B 打气 (A gěi B dǎ qì) : Ai cổ vũ cho ai.\r\nCâu này dùng để khích lệ những người không có tự tin làm một việc gì đó.', NULL, NULL);
INSERT INTO `tips` VALUES (36859, 'Chữ 君 Quân là một chữ hội ý kết cấu trên dưới, phần trên là biểu\r\ntượng của bàn tay cầm quyền (chữ 尹 Doãn cũng có nghĩa là cai trị), nét phẩy là biểu tượng\r\ncủa ngọn roi, cũng chính là quyền lực uy phong, tri thức và phẩm hạnh của đấng minh quân giữ\r\ntrọng trách trị vì thiên hạ. Bộ 口 (miệng) bên dưới biểu thị sự sai khiến bằng mệnh lệnh.\r\nKết hợp hai yêu tố trên và dưới, cùng thể hiện một ý nghĩa: đấng minh quân là người dùng\r\nquyền uy sai khiến, trị vì thiên hạ, chỉ cần lên tiếng mà chưa cần ra tay.', NULL, NULL);
INSERT INTO `tips` VALUES (36860, 'Hãy học tiếng Trung bằng nhiều cách khác nhau như: xem phim, nghe nhạc,...', NULL, NULL);
INSERT INTO `tips` VALUES (36861, 'Bài thi HSK 3 khác với bài thi HSK 2 là có thêm phần viết. Thời gian làm bài thi\r\nHSK 3 là 90 phút. Phần nghe 40 câu trong vòng 35 phút. Phần đọc hiểu gồm 30 câu trong vòng 20 phút. Cuối\r\ncùng là phần Viết với 10 câu trong vòng 15 phút. Tương tự như HSK 2, bạn cũng 5 phút dùng để điền thông tin\r\ncơ bản và 5 phút còn lại để điền đáp áp vào giấy trả lời.', NULL, NULL);
INSERT INTO `tips` VALUES (36862, 'Bộ thủ ( 力 )\r\n- Từ chứa bộ thủ này thường có liên quan tới sức lực hoặc lao động.\r\n- Ví dụ: 动、功、加... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36863, 'Bộ thủ ( 辶 )\r\n- Từ chứa bộ thủ này thường có liên quan tới đi lại hoặc hành động.\r\n- Ví dụ: 远、近... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36864, 'Bộ thủ ( 走)\r\n- Từ chứa bộ thủ này thường có liên quan tới đi lại hoặc các động tác liên quan tới chân.\r\n- Ví dụ: 起、赶、趁、越 ... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36865, '”毛手毛脚” Trong tiếng trung hiện đại có 2 nghĩa , một là biểu thị\r\nnhững người làm việc “tay chân vụng về, cẩu thả, chân tay lóng ngóng”, hai là chỉ\r\nnhững hành vi “tùy ý đụng chạm người khác”', NULL, NULL);
INSERT INTO `tips` VALUES (36866, 'Người Trung Quốc kiêng tặng ô vì đồng âm ở Trung Quốc, từ ‘ô’ (伞 sǎn) đồng âm với\r\ntừ ‘tách’ (散 sàn). Vì vậy, nó là một điều báo trước cuộc chia ly.', NULL, NULL);
INSERT INTO `tips` VALUES (36867, 'Nếu bạn đang học theo một bộ giáo trình nào đó, một bài khoá thường đi kèm bảng từ vựng, mục đích tác giả\r\nđưa bài khoá vào bài học là để các bạn hiểu đúng và học được cách dùng từ, cấu trúc ngữ pháp vào đúng ngữ\r\ncảnh, vì vậy đừng học riêng lẻ từng từ một, hãy gắn chúng với ngữ cảnh cụ thể khi học.', NULL, NULL);
INSERT INTO `tips` VALUES (36868, 'Loại lỗi sai này bao gồm: sự kết hợp không phù hợp giữa Chủ ngữ và Vị ngữ, giữa Vị ngữ và Tân ngữ, giữa Chủ\r\nngữ và Tân ngữ, giữa Từ bổ sung ý nghĩa và Trung tâm ngữ. Mục đích của loại lỗi sai này là kiểm tra thí sinh\r\ncó hiểu rõ, nắm chắc được cách dùng của các mẫu câu, từ vựng trong tiếng Trung hay không.', NULL, NULL);
INSERT INTO `tips` VALUES (36869, 'Cách phát hiện lỗi sai:\r\n- Cách hiệu quả nhất để phát hiện lỗi sai dạng này là tìm ra Chủ ngữ, Vị ngữ, Tân ngữ của câu, sau đó kiểm\r\ntra xem các thành phần này kết hợp với nhau đã phù hợp chưa.\r\n- Nếu các thành phần chính của câu không xuất hiện lỗi sai thì chúng ta sẽ tiếp tục tìm ra các thành phần\r\nphụ trong câu gồm Từ bổ sung ý nghĩa và Trung tâm ngữ, xem hai thành phần này có thể kết hợp được với nhau\r\nkhông.', NULL, NULL);
INSERT INTO `tips` VALUES (36870, 'Mách các bạn đang chuẩn bị thi HSK5:\r\nCách làm câu 100:\r\nPhần yêu cầu mọi người xem lại câu 99.\r\nĐể làm được câu 100 mọi người cần nhìn tranh phân tích ra 6 yếu tố, bao gồm:\r\n- Thời gian: Không có có thể bịa.\r\n- Địa điểm: Không có có thể bịa.\r\n- Nhân vật: (Thông thường khi viết có hai hướng, ngôi thứ nhất hóa thân thành nhân vật hoặc cách\r\nviết chung.)\r\n- Sự vật.\r\n- Động tác.\r\n- Biểu cảm.', NULL, NULL);
INSERT INTO `tips` VALUES (36871, 'Mách các bạn một số mẹo để nhớ chữ Hán một cách nhanh nhất.\r\n- Học chữ Hán qua bộ thủ: ví dụ như các bộ 女, 子, 人...\r\n- Học qua biểu nghĩa: ví dụ các chữ có bộ 水 thì sẽ liên quan đến sông, nước...\r\n- Học qua biểu âm: ví dụ những chữ có bộ 生 như 牲, 笙, 栍, 泩, 苼, 狌 đều được đọc là “shēng”...\r\n- Học qua thơ\r\n- Học qua hình ảnh', NULL, NULL);
INSERT INTO `tips` VALUES (36872, 'Bộ thủ ( 止 )\r\n- Từ chứa bộ thủ này thường có liên quan tới ngón chân.\r\n- Ví dụ: 步，趾...', NULL, NULL);
INSERT INTO `tips` VALUES (36873, 'Tứ đại mỹ nam Trung Quốc gồm Phan An, Tống Ngọc, Lan Lăng Vương, Vệ Vương Giới.', NULL, NULL);
INSERT INTO `tips` VALUES (36874, '啊 (a) có cách nói khác như 呀 (ya)、哇 (wa).', NULL, NULL);
INSERT INTO `tips` VALUES (36875, 'Nghệ thuật cắt giấy Trung Quốc là nghệ thuật dùng dao hoặc kéo để khắc hoa văn\r\nhình ảnh trên giấy, giấy được cắt ra dùng để trang trí nhà, tường hoặc dùng trong các dịp lễ tết, chúc thọ,\r\nhỉ khi phối hợp với các văn hóa dân gian. Nghệ thuật cắt giấy có lịch sử lâu đời, khởi nguyên từ thời Tây\r\nHán trước công nguyên với hình sức ban sơ là cắt hoa trên da động vật hoặc mảnh đồng.', NULL, NULL);
INSERT INTO `tips` VALUES (36876, 'Lỗi sai trật tự từ trong HSK6\r\nTìm ra trung tâm ngữ và từ bổ sung ý nghĩa cho trung tâm ngữ (trạng ngữ, định ngữ) và phán đoán quan hệ bổ\r\nsung ý nghĩa giữa chúng đã chính xác chưa, có xuất hiện tình trạng đảo lộn vị trí giữa trung tâm ngữ và từ\r\nbổ sung ý nghĩa hay không.\r\n- Chú ý trật tự của trạng ngữ. Ở tiếng Trung, trạng ngữ chỉ thời gian thường đứng trước trạng ngữ chỉ địa\r\nđiểm.\r\n- Kiếm tra thành phần bổ sung ý nghĩa trong câu là trạng ngữ hay định ngữ để xem vị trí của nó đã đúng ngữ\r\npháp chưa.', NULL, NULL);
INSERT INTO `tips` VALUES (36877, 'Quy tắc 90 90 1\r\nChuyên gia Robin Sharma có gợi ý một kế hoạch giúp nhận ra ý tưởng hoặc giấc mơ của bạn: quy tắc 90-1-1. Ông\r\nviết:\r\n\'Trong 90 ngày tới, dành 90 phút đầu tiên của ngày làm việc cho một cơ hội tốt nhất trong cuộc đời bạn.\r\nKhông làm gì khác. Đừng để điều gì gây phiền nhiễu. Chỉ hoàn thành dự án đó thôi.\'\r\nĐiều này có nghĩa: Đăng xuất khỏi email, bật chế độ im lặng của điện thoại, báo “Bận” hay để ẩn trong ứng\r\ndụng trò chuyện, làm bất cứ điều gì bạn cần để không bị làm phiền. Sau đó, dù cho là dự án, tầm nhìn hay ý\r\ntưởng gì chăng nữa, hãy tập trung vào nó trong 90 phút. Làm điều này trong 3 tháng, đảm bảo bạn sẽ đạt được\r\nthành quả đáng kể cho những gì bạn mơ ước.\r\nNếu 90 phút (hoặc 90 ngày) có vẻ khó khăn với bạn, hãy cố gắng bắt đầu quy tắc này với 30-30-1 hoặc 45-45-1.\r\nNicole Antoinette từ Life Less Bullshit thậm chí còn cho rằng chỉ cần 13 phút mỗi ngày cho những dự án bạn\r\nđam mê là đủ.\r\nVậy nên, để trau dồi một kỹ năng không quá khó, chỉ là bạn có dám đầu tư cho nó hay không mà thôi.', NULL, NULL);
INSERT INTO `tips` VALUES (36878, 'Bộ thủ ( 冖 )', NULL, NULL);
INSERT INTO `tips` VALUES (36879, '𝐿𝑢𝑦𝑒̣̂𝑛 𝑛𝑜́𝑖 𝑡ℎ𝑢̛𝑜̛̀𝑛𝑔 𝑥𝑢𝑦𝑒̂𝑛\r\nTất cả các phương pháp trên sẽ không hiệu quả nếu bạn không luyện nói thường xuyên. Tự chọn chủ đề, soạn nội\r\ndung trọng tâm và triển khai nói. Đứng nói trước gương, ghi âm giọng nói, luyện nói với bạn bè,… đều là\r\nnhững cách giúp bạn tự tin hơn và được luyện tập nhiều hơn. Nếu bạn may mắn làm quen với bạn bè hay được làm\r\nviệc với người TQ, thì còn gì tuyệt vời hơn để luyện nói. Hãy luyện tập nhiều nhất có thể, bạn chắc chắn sẽ\r\nnhận ra sự tiến bộ từng ngày.\r\nVà đừng quên, nỗ lực bạn bỏ ra ít, thành quả thu về cũng ít và ngược lại. Cố lên bạn nhé!', NULL, NULL);
INSERT INTO `tips` VALUES (36880, 'Bộ thủ ( 女 )\r\n- Từ chứa bộ thủ này thường có liên quan tới nữ hoặc dòng họ.\r\n- Ví dụ: 姐、妹、妈、奶... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36881, 'Câu hỏi có từ 吗 được đọc lên giọng.', NULL, NULL);
INSERT INTO `tips` VALUES (36882, 'Tứ đại cố đô Trung Quốc gồm Bắc Kinh, Nam Kinh, Lạc Dương và Tây An.', NULL, NULL);
INSERT INTO `tips` VALUES (36883, 'Uống trà, trồng trà và chế tác trà đều có nguồn gốc từ Trung Hoa. Trong sử sách\r\nTrung Hoa ghi lại, cách đây 7000 năm trước, thời đại Thần Nông, người Trung Quốc đã phát hiện ra lá trà, hơn\r\nnữa họ còn phát hiện ra lá trà có tác dụng làm thuốc.', NULL, NULL);
INSERT INTO `tips` VALUES (36884, 'Động từ trong tiếng Trung phần lớn mang được tân ngữ:', NULL, NULL);
INSERT INTO `tips` VALUES (36885, 'Tứ đại tài nữ Trung Quốc gồm Thái Diễm (Thái Văn Cơ), Lý Thanh Chiếu, Trác Văn\r\nQuân, Thượng Quan Uyển Nhi.\r\n\'小姐\' là tên gọi chung của cô gái ở Đài Loan, ở Trung Quốc \'小姐\' thì tùy các khu\r\nvực , có nơi là sử dụng bình thường, ở một số nơi thì là chỉ gái ngành, nếu dùng từ này thì nên thêm vào tên\r\nhọ như 陈小姐、王小姐、李小姐 hoặc có thể gọi bằng 小姐姐、美女, trong những trường hợp trang trọng thì cũng có thể gọi bằng\r\n女士.', NULL, NULL);
INSERT INTO `tips` VALUES (36886, 'Để thi HSK4, bạn cần trang bị cho mình khoảng 1200 từ vựng tiếng Trung.', NULL, NULL);
INSERT INTO `tips` VALUES (36887, 'Quy tắc ngoài trước trong sau:\r\nChữ Hán cũng ảnh hưởng rất nhiều từ văn hóa của quốc gia này. Quy tắc viết bên ngoài trước bên trong sau\r\ngiống như việc xây cung thành thì phải xây thành bảo vệ trước rồi mới đến bên trong . Lấy vị dụ là chữ “用”', NULL, NULL);
INSERT INTO `tips` VALUES (36888, 'Cấu trúc bị lẫn lộn trong HSK6\r\nMột dạng câu thường gặp, có hai kiểu cấu trúc bị lẫn lộn. Kiểu thứ nhất là người viết sử dụng hai cấu trúc\r\ncó ý nghĩa giống nhau hoặc gần giống nhau để viết câu. Kiểu thứ hai là người viết sử dụng hai cấu trúc có ý\r\nnghĩa khác nhau để viết câu.', NULL, NULL);
INSERT INTO `tips` VALUES (36889, 'Cách phát hiện lỗi sai:\r\n- Với lỗi sai Cấu trúc lộn xộn, thí sinh cần chú ý: Khi nhìn thấy trong câu có cấu trúc thường sử dụng, cần\r\nsuy nghĩ kỹ hơn, quan sát xem liệu có bị lẫn lộn cấu trúc, đặc biệt là những cấu trúc như “从……出发” và\r\n“以……为基础”, khi gặp phải cấu trúc như trên thì cần kiểm tra xem từ ngữ phía trước và phía sau có tương ứng với\r\nnhau không. Ngoài ra, thí sinh cần nắm vững phương pháp tìm thành phần chính trong câu, sau khi đã tìm được\r\ncác thành phần chính thì rất dễ phát hiện ra những lỗi sai.', NULL, NULL);
INSERT INTO `tips` VALUES (36890, 'Kim văn là dạng chữ được khắc hoặc đúc trên dụng cụ bằng đồng thau\r\nvào thời Thương - Chu còn gọi là \'Chung đỉnh văn\' chữ trên chuông và đỉnh.\r\n\'恋床脑(liàn chuáng nǎo)\' chỉ những người yêu giường ngủ, có thể nằm bẹp trên giường\r\ncả ngày.', NULL, NULL);
INSERT INTO `tips` VALUES (36891, 'Đánh giá, cho điểm thí sinh dựa theo tiêu chí dưới đây:\r\n- Thi sinh sẽ đạt điểm số cao khi có thể diễn đạt lưu loát, mạch lạc, ngắt nghỉ hợp lý, ít\r\ntrùng lặp từ và nội dung phải phù hợp, liên quan tới bức tranh.\r\n- Thí sinh sẽ đạt điểm số trung bình khi câu trả lời về cơ bản liên quan tới bức tranh. Khi trả\r\nlời còn mắc lỗi ngữ pháp, ấp úng, trùng lặp câu từ.\r\n- Thí sinh sẽ đạt điểm số thấp khi nội dung không liên quan đến bức tranh. Các ý diễn đạt còn\r\nhạn chế, rời rạc, không mạch lạc, logic.', NULL, NULL);
INSERT INTO `tips` VALUES (36892, 'Tiểu Triện đây là dạng chữ thông dụng vào thời nhà Tần, nên còn gọi\r\nlà \'Tần Triện\'.', NULL, NULL);
INSERT INTO `tips` VALUES (36893, 'Chào buổi sáng 早上好 có thể rút gọn thành 早 hoặc 早安.', NULL, NULL);
INSERT INTO `tips` VALUES (36894, 'Danh từ luôn luôn đứng trước từ chỉ vị trí.', NULL, NULL);
INSERT INTO `tips` VALUES (36895, 'So sánh về THI MÁY và THI VIẾT\r\nThi máy: Thi nghe trên máy, mỗi thí sinh sẽ được trang bị một tai nghe riêng.\r\nThi giấy: Phần nghe khi thi giấy thù cả phòng nghe chung một loa.', NULL, NULL);
INSERT INTO `tips` VALUES (36896, 'Tượng hình là phép vẽ hình tượng của các vật để tạo nên chữ, tùy theo thể mà\r\nthêm bớt.\r\nVí dụ:日 Nhật ; 月 Nguyệt; 人 Nhân; 木 Mộc v.v...', NULL, NULL);
INSERT INTO `tips` VALUES (36897, 'CHỌN GIÁO TRÌNH PHÙ HỢP:\r\nViệc chọn được giáo trình phù hợp trong học tập giống như tìm thấy ngọn đèn trong đêm tối vậy. Theo như mình\r\ntham khảo thì một số loại giáo trình thường được sử dụng như sau:\r\n- Giáo trình hán ngữ (6 quyển): Đây là bộ sách được sử dụng rộng rãi kể cả trong các trường đại học với ưu\r\nđiểm là các bài học được sắp xếp đầy đủ và khoa học. Tuy nhiên, nó yêu cầu thời gian tương đối dài và nếu\r\ngiao tiếp cơ bản thì bản chỉ cần học đến quyển 3.\r\n- Giáo trình HSK tiêu chuẩn: Đây là bộ giáo trình chuyên sâu dành mà bất cứ bạn nào muốn ôn thi HSK đều nên\r\ncó.\r\n- Giáo trình 301: Chủ yếu là các câu đàm thoại tiếng Trung giúp rút ngắn quá trình học, phù hợp với người đã\r\nđi làm.\r\n- Giáo trình Boya: Chú trọng nhiều về ngữ pháp và các bài học mang tính đúc kết.\r\n- Giáo trình YCT: Đây là giáo trình phù hợp cho những bạn nhỏ, học sinh tiểu học học tiếng Trung.\r\n- Giáo trình Mstong: Giáo trình được cập nhật từ vựng mới.', NULL, NULL);
INSERT INTO `tips` VALUES (36898, 'Thi HSK cấp mấy thì phù hợp?\r\nKhi mới bắt đầu học tiếng Trung nhiều bạn thường phân vân không biết ngôn ngữ này có khó học không. Học\r\ntrong bao lâu thì có thể giao tiếp được. Những bạn du học sinh sắp đi du học hoặc người lao động có nhu cầu\r\nthi HSK cũng rất lo lắng về độ khó của bài thi HSK. Thi HSK cấp mấy thì phù hợp.\r\nĐể xác định cấp thi HSK phù hợp, bạn cần biết được mục tiêu thi HSK hay mục tiêu học tiếng Trung của bản\r\nthân. Nếu bạn chỉ cần giao tiếp tiếng Trung cơ bản, các chủ đề xoay quanh cuộc sống thường ngày. Hoặc bạn\r\nmuốn học tiếng Trung để đi du lịch, hiểu các trang web bán hàng Trung Quốc thì chỉ cần trình độ tương đương\r\nHSK 2 hoặc 3 là đủ.\r\nTuy nhiên, nếu bạn muốn học tiếng Trung để đi du học, xin việc hoặc tìm kiếm cơ hội thăng tiến thì nên có\r\nchứng chỉ HSK cấp 4 trở lên. HSK càng cao thì khả năng bạn được học bổng du học, được tuyển dụng và thăng\r\ntiến càng lớn.\r\nHầu hết các trường Đại học tại Trung Quốc đều yêu cầu sinh viên phải có bằng HSK 4 đối với bậc Đại học và\r\nHSK 5 trở lên đối với Thạc sĩ.\r\nTrở lại với chủ đề chính, nhiều bạn nghĩ việc thi HSK sẽ tuần tự từ H1-H6, nhưng không phải vậy. Bạn có thể\r\nlựa chọn cấp độ phù hợp với năng lực của bản thân để thử sức.', NULL, NULL);
INSERT INTO `tips` VALUES (36899, '“鞭长不及马腹(biān cháng bùjí mǎ fù) Sức ngoài tầm với.', NULL, NULL);
INSERT INTO `tips` VALUES (36900, '个 (gè) là lượng từ được dùng rộng rãi nhất, khi dịch phải căn cứ vào danh từ để\r\ndịch nghĩa lượng từ cho sát.', NULL, NULL);
INSERT INTO `tips` VALUES (36901, '上 (shàng): động từ nghĩa là: lên ==》上课 (shàngkè): lên lớp, vào học\r\n下 (xià): động từ nghĩa là: xuống ==&gt; 下课 (xiàkè) Tan học', NULL, NULL);
INSERT INTO `tips` VALUES (36902, '\'容貌焦虑\' ngôn ngữ mạng \'ám ảnh ngoại hình\'\r\n\'站得高，看得远\' mang nghĩa \'đứng càng cao, nhìn càng xa\'', NULL, NULL);
INSERT INTO `tips` VALUES (36903, 'Tứ đại mỹ nhân Trung Quốc cổ đại theo cách nói thông thường gồm có: Tây Thi,\r\nVương Chiêu Quân, Điêu Thuyền và Dương Ngọc Hoàn.', NULL, NULL);
INSERT INTO `tips` VALUES (36904, 'Không ậm ừ, ngắt quãng, lặp lại.', NULL, NULL);
INSERT INTO `tips` VALUES (36905, '没有爱的生活就像一棵不开花、不结果的树。\r\nMéiyou ài de shēnghuó jiù xiàng yì kē bù kāihuā, bù jiéguǒ de shù.\r\nCuộc sống mà không có tình yêu cũng giống như một cái cây không nở hoa, không kết quả vậy.', NULL, NULL);
INSERT INTO `tips` VALUES (36906, 'Để nói tiếng Trung như người bản địa, đòi hỏi bạn phải nghe thật nhiều và thực\r\nhành kĩ năng nói theo ngữ điệu và phát âm của họ.', NULL, NULL);
INSERT INTO `tips` VALUES (36907, 'Đầu tiên, bạn cần xác định bạn muốn nói tiếng Trung giọng Bắc Kinh hay Thượng Hải hoặc tỉnh nào mà bạn yêu\r\nthích. Sau đó, hãy chọn những đoạn hội thoại trên internet và bắt đầu thực hành. (Giọng Bắc Kinh có film\r\n北京故事） Lưu ý: cách tốt nhất để rèn luyện kỹ năng nghe là tạm dừng và lặp lại những gì người nói đang nói.\r\nHoặc nói đuổi theo người nói mà không cần dừng lại, ghi âm giọng nói của chính mình để so sánh với giọng\r\nngười nói.', NULL, NULL);
INSERT INTO `tips` VALUES (36908, 'Đọc thơ đoán từ vựng:\r\n\'Chữ lập (立) đập chữ viết(曰),\r\nChữ viết (曰) đập chữ thập (十).\'\r\nĐây là chữ gì?', NULL, NULL);
INSERT INTO `tips` VALUES (36909, 'Trong khẩu ngữ \'媳妇\' có nghĩa là “vợ “hoặc là”Con dâu”. Nhưng các\r\nđiều khoản trong pháp luật của Trung Quốc thì chỉ công nhận \'媳妇\' chỉ có nghĩa là con dâu thôi.\r\nKhi nghĩa là chỉ “vợ “ thì thường sẽ thêm vào 儿话音, như “媳妇儿”, không thêm 儿话音 thì có nghĩa là con dâu.', NULL, NULL);
INSERT INTO `tips` VALUES (36910, 'Lỗi sai khi dùng liên từ HSK6\r\nLiên từ thường xuất hiện trong câu phức, thể hiện quan hệ logic của hai vế câu. Trong bài tìm câu sai, các\r\ncâu có liên từ cũng thường xuyên được chọn để đặt lỗi sai.', NULL, NULL);
INSERT INTO `tips` VALUES (36911, 'Cách nhận biết lỗi sai:\r\nNgười ra đề thường đưa ra các dạng đề như: sử dụng\r\nsai liên từ; thiếu liên từ và đặt sai vị trí của liên từ và chủ ngữ. Với ba vấn đề này, khi nhìn thấy câu\r\nphức, chúng ta cần làm theo các bước sau:\r\n- Bước 1, phân tích quan hệ logic của hai vế câu, kiểm tra liên từ xem đã thể hiện được mối quan hệ này hay\r\nchưa, đồng thời kiểm tra thứ tự của hai vế câu có hợp lý không.\r\n- Bước 2, kiểm tra xem liên từ có bị thiếu hay không.\r\n- Bước 3, chú ý vị trí của liên từ và chủ ngữ. Nếu chủ ngữ của hai vế câu giống nhau, liên từ sẽ đặt sau chủ\r\nngữ. Nếu\r\nchủ ngữ hai vế câu khác nhau, liên từ sẽ đặt trước chủ ngữ.', NULL, NULL);
INSERT INTO `tips` VALUES (36912, 'Lịch dương được tính dựa theo chu kỳ quay của Trái Đất xung quanh mặt trời. Trong\r\ntiếng Hán, Mặt Trời còn được gọi là Thái Dương. Vì thế, dương lịch còn được gọi là lịch Thái Dương.', NULL, NULL);
INSERT INTO `tips` VALUES (36913, 'Câu bị tối nghĩa\r\nCâu có ý nghĩa không rõ ràng, dễ gây hiểu lầm. Các nguyên nhân chính dẫn đến câu bị tối nghĩa là do người\r\nviết chưa nắm chắc kiến thức ngữ pháp, ngữ nghĩa, từ vựng.', NULL, NULL);
INSERT INTO `tips` VALUES (36914, 'Cách phát hiện lỗi sai:\r\n- Có rất nhiều nguyên nhân dẫn đến câu tối nghĩa, thí sinh nên tích lũy các dạng câu tối nghĩa điển hình để\r\ncó thể phòng tránh trong lúc tham gia cuộc thi. Các dạng câu điển hình gồm có: đại từ không chỉ rõ, giới hạn\r\nsố lượng không rõ ràng, phạm vi liệt kê không rõ ràng, ...\r\n- Ngoài ra trong lúc làm bài, thí sinh cần chú ý xem câu đó có từ đa nghĩa hay không, nếu có từ đa nghĩa và\r\nngữ cảnh của câu không thể làm rõ một ý nghĩa của từ đa nghĩa thì chúng ta có thể kết luận đây là câu sai.', NULL, NULL);
INSERT INTO `tips` VALUES (36915, 'Bộ thủ ( 日)\r\n- Từ chứa bộ thủ này thường có liên quan tới mặt trời hoặc thời gian.\r\n- Ví dụ: 明、昨、晴... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36916, 'Phần thi miêu tả tranh chúng ta cần quan sát tỉ mỉ và trả lời 5 câu\r\nhỏi dưới đây:\r\n- 他/她是谁？\r\n- 他/她在那里？\r\n- 他/她在干什么？\r\n- 他/她为什么这么做？\r\n- 后来怎么样了？', NULL, NULL);
INSERT INTO `tips` VALUES (36917, 'Bộ thủ ( 攵 )\r\n- Từ chứa bộ thủ này thường có liên quan tới đánh hoặc gõ.\r\n- Ví dụ: 放、收、败... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36918, 'Hãy giao tiếp thật nhiều hoặc ngay cả luyện nói trước gương bạn nhé.', NULL, NULL);
INSERT INTO `tips` VALUES (36919, 'Thanh 4 có kí hiệu trong bính âm (pinyin) là một nét xiên như dấu huyền trong\r\ntiếng Việt “”. Khi phát âm âm thanh sẽ đi từ âm vực cao xuống âm vực thấp. Trường độ ngắn . Đọc dứt khoác ,\r\nnhanh .', NULL, NULL);
INSERT INTO `tips` VALUES (36920, 'Giáp cốt văn là dạng chữ viết của thời nhà Thương. Khắc trên mai rùa\r\nhoặc xương thú.', NULL, NULL);
INSERT INTO `tips` VALUES (36921, 'Mỗi câu chỉ được nghe một lần thôi nên bạn hãy cố gắng thật tập trung nhé!', NULL, NULL);
INSERT INTO `tips` VALUES (36922, 'Bộ thủ ( 礻)\r\n- Từ chứa bộ thủ này thường có liên quan tới thần linh, việc thờ cúng, họa phúc... ...\r\n- Ví dụ: 视, 福.. ..', NULL, NULL);
INSERT INTO `tips` VALUES (36923, 'Bộ thủ ( 衤 )\r\n- Từ chứa bộ thủ này thường có liên quan tới quần áo, trang phục.\r\n- Ví dụ: 裙、衬、被... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36924, 'Để mở lời xin trả lời câu hỏi 11, và cảm ơn khi kết thúc câu hỏi số\r\n14 ta có thể sử dụng mẫu câu tham khảo sau:\r\n- 尊敬的各位老师，您好。题目已收到，我开始答题。(Zūnjìng de gèwèi lǎoshī, nín hǎo. Tímù yǐ shōu dào, wǒ kāishǐ dátí.)\r\n- 答题完毕，感谢各位老师。(Dátí wánbì, gǎnxiè gèwèi lǎoshī.)', NULL, NULL);
INSERT INTO `tips` VALUES (36925, '怎么样 (zěnmeyàng) luôn đứng cuối câu.', NULL, NULL);
INSERT INTO `tips` VALUES (36926, 'Bộ thủ ( 囗)\r\n- Từ chứa bộ thủ này thường có liên quan tới vây hãm, bao vây.\r\n- Ví dụ: 国, 困... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36927, 'Chữ BÍNH 丙 cổ nhất chỉ đuôi cá, trong sách Nhĩ nhã, bộ từ điển giải thích nghĩa\r\nchữ đầu tiên của Trung Quốc có nói:“ Ngư vĩ, vị chi bính ( cái đuôi cá gọi là bính). Nghĩa gốc đã mất từ\r\nlâu, nay người ta dùng BÍNH để chỉ vị trí thứ ba trong Thiên can.\r\n\'母胎单身\' ngôn ngữ mạng \'ế từ trong bụng mẹ\'', NULL, NULL);
INSERT INTO `tips` VALUES (36928, 'Bộ thủ ( 见 )\r\n- Từ chứa bộ thủ này thường có liên quan tới nhìn thấy.\r\n- Ví dụ:观、视、......', NULL, NULL);
INSERT INTO `tips` VALUES (36929, 'Sai trong kết cấu liệt kê\r\nCách phát hiện lỗi sai:\r\n- Bước 1：Phán đoán các thành phần được liệt kê có thể được liệt kê cùng nhau hay không. Thông thường nguyên\r\nnhân khiến các thành phần không thể liệt kê cùng nhau được gồm:\r\n1. Một thành phần trong cấu trúc liệt kê không cùng nhóm với các thành phần còn lại\r\n2. Một thành phần trong cấu trúc liệt kê không cùng cấp độ.\r\n- Bước 2: Kiểm tra các thành phần liệt kê đã được liệt kê theo đúng logic chưa.\r\n\'世上无难, 只怕有人心\' mang nghĩa \'không có việc gì khó, chỉ sợ lòng không bền\'', NULL, NULL);
INSERT INTO `tips` VALUES (36930, 'Bộ thủ ( 石 )\r\n- Từ chứa bộ thủ này thường có liên quan tới đất đá hoặc những vật làm từ đất đá.\r\n- Ví dụ: 碎、碗、破... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36931, '就算全世界将我遗忘，我只要你记得我的存在。\r\nJiùsuàn quán shìjiè jiāng wǒ yíwàng, wǒ zhǐyào nǐ jìdé wǒ de cúnzài.\r\nCho dù cả thế giới đều quên em, em chỉ cần anh nhớ đến sự tồn tại của em.', NULL, NULL);
INSERT INTO `tips` VALUES (36932, '\'伤害性不高，侮辱性极强\' ngôn ngữ mạng \'Tính sát thương không cao nhưng bách nhục\'', NULL, NULL);
INSERT INTO `tips` VALUES (36933, 'So sánh về THI MÁY và THI VIẾT\r\nThi máy: Với phần thi viết trên máy, bạn chỉ cần đánh pinyin.\r\nThi giấy: Bạn bắt buộc phải nhớ mặt chữ mới có thể làm được bài.', NULL, NULL);
INSERT INTO `tips` VALUES (36934, 'Bộ thủ ( 斤 )\r\n- Từ chứa bộ thủ này thường có liên quan tới rìu và hành động chặt, gọt.\r\n- Ví dụ: 新，所... ..', NULL, NULL);
INSERT INTO `tips` VALUES (36935, '这个(家)没我不行\r\nNỗ lực gánh cả đội (gánh còng lưng)', NULL, NULL);
INSERT INTO `tips` VALUES (36936, 'Khi phát âm “zh” hãy để lưỡi cong lên phía vòm trên (ngạc trên) và phát âm chữ\r\n\'ch\' tiếng Việt, để kiểm tra thì các bạn đọc thêm chữ “i” sau khi đọc “zh” nhé, lúc đó các bạn sẽ phát hiện\r\nra sự thay đổi của lưỡi.', NULL, NULL);
INSERT INTO `tips` VALUES (36937, 'Luôn cố gắng cải thiện kĩ năng nghe.', NULL, NULL);
INSERT INTO `tips` VALUES (36938, 'Mách các bạn đang chuẩn bị thi HSK5:\r\nYêu cầu câu 99:\r\n- Cho trước 5 từ vựng, không phân thứ tự trước sau.\r\n- Chủ đề rõ ràng, kết cấu hoàn chỉnh.\r\n- Viết thành 1 hoặc 2 đoạn, phía trước mỗi đoạn lùi vào hai ô, viết từ 80-100 từ là an toàn\r\n(bao gồm cả dấu).\r\n- Mỗi câu nên viết từ 10-20 chữ, không nên viết quá 20 chữ ( viết câu dài dễ sai ngữ pháp)\r\n- Sử dụng trợ động từ, phó từ, các cụm cố định hoặc thành ngữ (từ 1-2 thành ngữ).\r\n- Tránh lặp từ, lặp cấu trúc.\r\n- Kiểm tra lại dấu.\r\nCách phân tích đề:\r\nBước 1: Hiểu nghĩa từ, từ tính của từ là gì.\r\nVí dụ: (一致 mang nghĩa nhất trí về quan điểm khi là tính từ, mang nghĩa đều khi làm phó từ)\r\nBước 2: Xem từ suy ngữ pháp, kết cấu câu.\r\nBước 3: Đặt câu dựa theo ngữ pháp.\r\nBước 4: Viết bài.\r\n\'熟悉\' có hai cách đọc:\r\nNgười Trung Quốc đọc shúxi. Người Đài Loan đọc shóuxī.', NULL, NULL);
INSERT INTO `tips` VALUES (36939, 'Hành thư là một phong cách viết chữ Hán bắt nguồn từ thảo thư. Tuy nhiên, vì nó\r\nkhông quá tháu như thảo thư cho nên dễ đọc hơn, và hầu hết những người đọc được khải thư cũng đều có thể đọc\r\nđược hành thư.', NULL, NULL);
INSERT INTO `tips` VALUES (36940, 'Lưu ý khi luyện viết chữ Hán: Để học chữ viết chữ Hán một cách có hiệu quả việc\r\nđầu tiên các bạn cần nắm vững về các nét và thứ tự viết trong tiếng Trung. Một số lưu ý khi viết chữ Hán', NULL, NULL);
INSERT INTO `tips` VALUES (36941, '* Chữ Hán được quy định viết trong 1 ô vuông (đã có sẵn), chúng ta cần căn chỉnh sao cho hợp lý, vừa mắt để\r\ncó thể viết chữ gói gọn trong 1 ô như vậy.\r\n* Xây dựng tư thế viết chữ và cách cầm bút theo quy chuẩn nhất định. Đặt bút nhẹ hoặc nhấn mạnh đậm nhạt tùy\r\ntheo sở thích và yêu cầu.\r\n* Đưa bút nhẹ nhàng, thả lỏng tinh thần hình dung như bạn đang luyện viết tiếng mẹ đẻ. Lưu ý rằng chữ Hán\r\nmuốn đẹp trước tiên cần phải viết đúng. Do đó các nét chữ Hán cần viết rõ ràng: ví dụ nét ngang không được\r\nnhầm sang nét mác……\r\n* Khi đã viết xong, nhấc bút nhẹ nhàng, hoặc nhấn nhẹ để tạo các nét hất (nếu có)', NULL, NULL);
INSERT INTO `tips` VALUES (36942, 'Bộ thủ ( 玉 )\r\n- Xa xưa trong triện thư (tiểu triện) từ \'玉\' và \'王\' cách viết giống nhau nên chúng ta có thể\r\nthấy thiên bàng 王 cũng chính là 玉 này. Cho tới cuối thời Chiến Quốc người ta mới cho thêm một\r\nnét chấm vào \'玉\' để phân biệt với \'王\'.\r\n- Từ chứa bộ thủ này thường mô tả cái đẹp, tôn quý của sự vật.\r\n- Ví dụ: 王、国、环、班、琴、玩... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36943, '为什么喝酒？\r\n办公室的一名员工上班竟然在喝酒，经理看到的时候，心想这个人太过分了，便怒问：“你为什么在上班时间喝酒？\r\n这名员工答：“对不起，经理，我这是在纪念最后一次加薪10周年。', NULL, NULL);
INSERT INTO `tips` VALUES (36944, 'Wèi shénme hējiǔ?\r\nBàngōngshì de yī míng yuángōng shàngbān jìngrán zài hējiǔ, jīnglǐ kàn dào de shíhòu, xīn xiǎng zhège rén tài\r\nguòfènle, biàn nù wèn:“Nǐ wèishéme zài shàngbān shíjiān hējiǔ?”\r\nZhè míng yuángōng dá:“Duìbùqǐ, jīnglǐ, wǒ zhè shì zài jìniàn zuìhòu yīcì jiā xīn 10 zhōunián.', NULL, NULL);
INSERT INTO `tips` VALUES (36945, 'Tại sao lại uống rượu？\r\nMột nhân viên văn phòng ngồi uống rượu trong giờ làm, lúc nhìn thấy, giám đốc nghĩ bụng người này thật quá\r\nđáng, bèn tức giận hỏi: “Tại sao cậu lại uống rượu trong giờ làm?”\r\nCậu nhân viên đáp: “ Xin lỗi sếp, tôi đang uống kỉ niệm lần tăng lương cuối cùng cách đây 10 năm thôi.”', NULL, NULL);
INSERT INTO `tips` VALUES (36946, '\'元宇宙(yuán yǔzhòu)\' Chỉ những người chìm đắm trong thế giới ảo, hư cấu.', NULL, NULL);
INSERT INTO `tips` VALUES (36947, 'Cấu tạo của chữ hình thanh gồm hai phần: phần hình và phần thanh.\r\nPhần hình gợi ý nghĩa của chữ Hán, phần thanh gợi ý cách phát âm chữ Hán.', NULL, NULL);
INSERT INTO `tips` VALUES (36948, 'HSK 1 là mức cơ bản nhất. Mặc dù vẫn nằm trong hệ thống cấp bậc của HSK nhưng\r\ntrình độ này thông thường không cần phải thi. HSK 1 cũng không được cấp chứng chỉ. Ở cấp độ này, trình độ\r\ntiếng Trung chỉ ở mức sơ cấp thấp.', NULL, NULL);
INSERT INTO `tips` VALUES (36949, 'Một số quy tắc đặc biệt khi viết chữ:\r\n*Đối với những chữ có bộ 辶 và bộ 廴 , khi viết sẽ ưu tiên viết bộ nằm trên đó trước , đến cuối cùng mới viết\r\nhai bộ thủ đó.\r\n*Đối với những chữ có nét chấm nằm bên trên bên tay phải, nét chấm đó sẽ viết sau cùng khi hoàn thành tất cả\r\ncác bộ.', NULL, NULL);
INSERT INTO `tips` VALUES (36950, 'Tứ đại tài tử Trung Quốc gồm Đường Bá Hổ, Chúc Tri Sơn, Văn Tranh Minh, Từ Trinh\r\nKhanh.', NULL, NULL);
INSERT INTO `tips` VALUES (36951, '重伤\r\n两个外伤病人在病房里聊天。\r\n一个人说：“我倒霉死了，昨天开着刚买的新车出去兜风，正得意着呢，突然看到马路前面有一块牌子，太远了，没看清楚上面写了什么东西。我就赶紧开过去，一看，之间牌子上写着‘前面有沟，请绕行’。可刚看完，我还没反应过来，就连人带车掉下去。”\r\n说到这儿，他停了停问：“老兄，你怎么也伤得这么重啊？你的伤是怎么回事啊？”\r\n那人突然狠狠地瞪了他一眼，说：“怎么回事？老子当时正在挖沟！”', NULL, NULL);
INSERT INTO `tips` VALUES (36952, 'Phiên âm\r\nZhòngshāng\r\nLiǎng gè wàishāng bìngrén zài bìngfáng lǐ liáotiān.\r\nYīgè rén shuō:“Wǒ dǎoméi sǐle, zuótiān kāizhe gāng mǎi de xīnchē chūqù dōufēng, zhèng déyì zhene, túrán kàn\r\ndào mǎlù qiánmiàn yǒu yīkuài páizi, tài yuǎnle, méi kàn qīngchǔ shàngmiàn xiěle shénme dōngxī. Wǒ jiù gǎnjǐn\r\nkāi guòqù, yī kàn, zhī jiān páizi shàng xiězhe ‘qiánmiàn yǒu gōu, qǐng rào xíng’. Kě gāng kàn wán, wǒ hái\r\nméi fǎnyìng guòlái, jiù lián rén dài chē diào xiàqù.”\r\nshuō dào zhè’er, tā tíngle tíng wèn:“Lǎoxiōng, nǐ zěnme yě shāng dé zhème zhòng a? Nǐ de shāng shì zěnme huí\r\nshì a?”\r\nNà rén túrán hěn hěn de dèngle tā yīyǎn, shuō:“Zěnme huí shì? Lǎozi dāngshí zhèngzài wā gōu!”', NULL, NULL);
INSERT INTO `tips` VALUES (36953, 'Dịch nghĩa\r\nBị thương nặng\r\nHai bệnh nhân nằm trong phòng bệnh trò chuyện.\r\nMột người nói: “Tôi thật đen đủi mà, hôm qua tôi vừa lái con xe mới mua đi hóng gió, đang lúc vui vẻ, thì\r\nnhìn thấy phía trước đường có 1 tấm biển, vì xa quá không nhìn rõ trên đó viết cái gì. Thế nên tối liền lái\r\nxe đến đó, thấy trên biển có viết “Phía trước có hố, xin đi lối rẽ”. Tôi vừa mới xem xong, còn chưa kịp phản\r\nứng gì thì cả người lẫn xe đã rơi xuống hố rồi!”\r\nKể tới đây, anh ta dừng lại hỏi: “Anh bạn, sao anh lại bị thương nặng vậy? Anh làm sao mà bị thương thế?”\r\nNgười kia đột nhiên trừng mắt lên nói: “ Làm sao à? Ông mày lúc ấy đang đào hố!”', NULL, NULL);
INSERT INTO `tips` VALUES (36954, 'Thảo thư xuất hiện sớm hơn Khải thư, tức vào khoảng đầu nhà Hán.\r\nThời kỳ đầu, Thảo thư là biến thể của chữ Lệ viết nhanh, nên có tên \'Thảo lệ\', sau gọi là\r\n\'Chương Thảo\'.', NULL, NULL);
INSERT INTO `tips` VALUES (36955, 'Câu hỏi có từ 吧 được đọc xuống giọng.', NULL, NULL);
INSERT INTO `tips` VALUES (36956, 'Thanh nhẹ không chỉ là hiện tượng biến đổi âm mà còn có chức năng\r\nphân biệt nghĩa của từ.', NULL, NULL);
INSERT INTO `tips` VALUES (36957, 'Muốn phát âm như người bản xứ ư, hãy học cách đặt lưỡi ngay nhé.', NULL, NULL);
INSERT INTO `tips` VALUES (36958, 'Người Trung Quốc kiêng tặng đồ sắc nhọn trong dịp năm mới vì hàm ý rằng mối quan\r\nhệ sẽ bị cắt đứt. Nếu bạn đưa những thứ này cho người khác, có nghĩa là bạn sẽ chia tay với người nhận nó.', NULL, NULL);
INSERT INTO `tips` VALUES (36959, 'Hoa mẫu đơn là đại diện cho sự giàu sang và phú quý. Chính vì vậy mà bạn sẽ\r\nkhông thấy làm ngạc nhiên khi loài hoa này xuất hiện trong các lễ khai trương với mong muốn đem đến sự giàu\r\ncó buôn may bán đắt.\r\nNgoài ra có thể ít ai biết, mỗi màu hoa lại tượng trưng cho những ý nghĩa khác nhau.\r\nMẫu đơn đỏ: Biểu tượng cho danh dự và sự tôn trọng.\r\nMẫu đơn trắng: Thể hiện sự chân thành, tử tế, hài hòa và mộc mạc nên được sử dụng trong hoa cầm tay ngày\r\ncưới.\r\nMẫu đơn hồng: Tượng trưng cho sự e ấp, đơn giản mà vô cùng quyến rũ.\r\nMẫu đơn tím: Tượng trưng cho người phụ nữ quyền lực, chung thủy, quyến rũ.\r\nMẫu đơn vàng: Tượng trưng cho tuổi trẻ rực rỡ, tươi sáng; thể hiện những đam mê, khát vọng bùng cháy và\r\nkhông bao giờ gục ngã trước khó khăn.', NULL, NULL);
INSERT INTO `tips` VALUES (36960, 'Bộ thủ ( 冫 )\r\n- Thường có liên quan tới nước, có lúc sẽ biểu thị nhiệt độ thấp, lạnh.\r\n- Ví dụ: 冰、冻、冷、冲... ...\r\nTại sao 冶炼 (yěliàn) mang nghĩa tinh luyện kim loại lại có bộ băng vậy? Tại sao không phải là bộ\r\nkim?\r\n- Thực ra nghĩa gốc của 冶(yě) chính là dung luyện kim loại, chính là dùng hiện tượng băng tan\r\nđể ví von với việc kim loại tan chảy.', NULL, NULL);
INSERT INTO `tips` VALUES (36961, 'Thiếu thành phần câu trong HSK6\r\nKết cấu của câu không hoàn chỉnh, thiếu một số thành phần cấu tạo câu. Loại lỗi sai này muốn kiểm tra thí\r\nsinh có nắm chắc các thành phần cấu tạo câu hay không. Những thành phần câu thường thiếu trong loại lỗi sai\r\nnày là chủ ngữ, vị ngữ và tân ngữ, nhưng cũng có trường hợp thiếu những thành phần khác ví dụ như giới từ\r\nhoặc liên từ.', NULL, NULL);
INSERT INTO `tips` VALUES (36962, 'Cách phát hiện lỗi sai:\r\n- Với dạng lỗi sai này, các bạn chỉ cần tìm ra được các thành phần chính trong câu gồm chủ ngữ, vị ngữ, tân\r\nngữ. Nếu câu thiếu một trong ba thành phần này thì câu đó chính là câu sai.', NULL, NULL);
INSERT INTO `tips` VALUES (36963, 'Bộ thủ ( 钅 )\r\n- Từ chứa bộ thủ này thường có liên quan tới tiền tài hoặc kim loại.\r\n- Ví dụ: 铁、钱、钢... ...\r\n\'黄鹤楼(huáng hè lóu)\' Hoàng hạc lâu là một trong bốn công trình kiến trúc nổi\r\ntiếng ở phía nam sông Dương Tử và là một trong 40 điểm du lịch nổi tiếng ở Trung Quốc.', NULL, NULL);
INSERT INTO `tips` VALUES (36964, 'Bộ thủ ( 雨)\r\n- Từ chứa bộ thủ này thường có liên quan tới hiện tượng thời tiết như mây, mưa.\r\n- Ví dụ: 雪，雾... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36965, 'Tết tới người hoa có câu \'除陈布新\' hay từ \'扫尘\' vì \'陈\' trong \'陈旧\' mang nghĩa cũ kỹ,\r\nđồng âm với \'尘\' bụi bặm. Vậy nên Tết đến ta cần dọn dẹp nhà cửa sạch sẽ để sẵn sàng đón những điều đẹp đẽ.', NULL, NULL);
INSERT INTO `tips` VALUES (36966, 'Nếu trước nguyên âm “u” không ghép với phụ âm, lúc viết phải thêm bán nguyên âm\r\n“w” ở phía trước:\'u\'-\'wu\'.', NULL, NULL);
INSERT INTO `tips` VALUES (36967, 'Trong số bốn ngàn chữ Giáp cốt đã thu thập được chỉ có một ngàn\r\nchữ là có thể đọc và giải thích được nghĩa.', NULL, NULL);
INSERT INTO `tips` VALUES (36968, 'Tứ đại thần y Trung Quốc:\r\n扁鹊 Biển Thước\r\n华佗 Hoa Đà\r\n张仲景 Trương Trọng Cảnh\r\n李时珍 Lý Thời Trân', NULL, NULL);
INSERT INTO `tips` VALUES (36969, 'Lệ thư là dạng chữ thông dụng vào thời nhà Hán, bắt đầu từ cuối\r\nTần tới thời Tam Quốc. Còn được gọi là \'Hán lệ\'...', NULL, NULL);
INSERT INTO `tips` VALUES (36970, 'Bộ thủ ( 𧾷)\r\n- Từ chứa bộ thủ này thường có liên quan tới chân hoặc các động tác liên quan tới chân.\r\n- Ví dụ: 踢、跳、跑... ...\r\n\'不可同日而语\' mang nghĩa không nên đặt sự vật ở các khoảng thời gian khác\r\nnhau vào cùng một điều kiện tời gian, ý chỉ khác biệt rất lớn.', NULL, NULL);
INSERT INTO `tips` VALUES (36971, 'Dựa vào tranh chúng ta cũng có thể quan sát dựa theo:\r\n1. Nhân vật\r\n2. Sự kiện\r\n3. Hoàn cảnh\r\n4. Thời gian\r\n5. Thần sắc, phong thái nhân vật\r\n6. Đạo cụ liên quan', NULL, NULL);
INSERT INTO `tips` VALUES (36972, 'Cố gắng đọc to thành tiếng', NULL, NULL);
INSERT INTO `tips` VALUES (36973, 'Luyện nghe tiếng Trung hiệu quả\r\nHiểu trình độ của mình và chọn bài nghe cho phù hợp: có thể nghe hiểu ít nhất 70%\r\nChọn nghe những chủ đề mình thích để không cảm giác bị gò bó và có thêm động lực.\r\nXem ngữ cảnh, dựa vào các đoạn bạn nghe hiểu để đoán ra cái từ mới mình chưa biết.\r\nLuyện thêm khả năng nói và biểu đạt bằng cách nghe xong thì tóm tắt sơ lại câu chuyện bằng tiếng Trung và\r\nghi âm lại.', NULL, NULL);
INSERT INTO `tips` VALUES (36974, 'Khi nói về ngày, trong văn viết chúng ta hay dùng 日 (rì), văn nói ta hay dùng 号\r\n(hào).\r\n二日 hoặc 二号: Mồng 2', NULL, NULL);
INSERT INTO `tips` VALUES (36975, '\'稻城亚丁(dàochéng yǎdīng)\' gồm ba đỉnh núi tuyết \'仙乃日、英迈勇，夏诺\' hoàn toàn tách biệt\r\nvà sắp xếp theo hình chữ \'Kim\'.', NULL, NULL);
INSERT INTO `tips` VALUES (36976, '“父亲 (fùqīn: Phụ thân, cha, bố, ba) là cách gọi quen thuộc của thời cổ đại và\r\nhiện đại. Ngoài ra, còn có những cách gọi khác như “公 (gōng)”, “翁 (wēng)”, “严君 (yánjūn)”, “爹 (diē)”, “老子\r\n(lǎozi)”.\r\nTừ xưa, người Trung Quốc đã rất coi trọng lễ nghĩa. Họ thường gọi bố của mình một cách kính trọng như thêm\r\n“家 (jiā)”, “老 (lǎo)”, “先 (xiān)” vào trước.', NULL, NULL);
INSERT INTO `tips` VALUES (36977, '我们所有的梦想都能成真, 如果我们有勇气去追求它们。\r\nWǒmen suǒyǒu de mèngxiǎng dōu néng chéng zhēn, rúguǒ wǒmen yǒu yǒngqì qù zhuīqiú tāmen.\r\nTất cả ước mơ của chúng ta có thể trở thành hiện thực, nếu chúng ta có can đảm để theo đuổi chúng.', NULL, NULL);
INSERT INTO `tips` VALUES (36978, 'Có hai hình thức lắng nghe: chủ động và thụ động. Lắng nghe tích cực có thể hiểu\r\nlà việc bạn tập trung lắng nghe để thực sự hiểu ý nghĩa đằng sau một câu tiếng Trung đang được nói. Nghe thụ\r\nđộng chỉ đơn giản là nghe những gì mà người nói đang nói, nhưng không thực sự cố gắng hiểu từng từ. Dường\r\nnhư có nhiều quan điểm cho rằng bạn có thể cải thiện khả năng nghe tiếng Trungthông qua việc nghe thụ động.\r\nTuy nhiên, đây chưa hẳn là điều đúng đắn. Trong thực tế, việc nghe thụ động không thể giúp bạn đọng lại được\r\ntừ vựng, thấu hiểu nghĩa các từ, v.v. khi không dành thời gian để thực sự cố gắng để hiểu ngôn ngữ mà bạn\r\nđang lắng nghe, bạn sẽ không thể ghi nhớ bất cứ điều gì.', NULL, NULL);
INSERT INTO `tips` VALUES (36979, 'Bộ thủ ( 页 )\r\n- Từ chứa bộ thủ này thường có liên quan tới khuôn mặt, vùng đầu của con người.\r\n- Ví dụ: 颜，须... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36980, '𝐴́𝑝 𝑑𝑢̣𝑛𝑔 𝑡𝑢̛̀ 𝑣𝑢̛̣𝑛𝑔 đ𝑎̃ ℎ𝑜̣𝑐 𝑣𝑎̀𝑜 𝑡ℎ𝑢̛̣𝑐 ℎ𝑎̀𝑛ℎ 𝑘𝑖̃\r\n𝑛𝑎̆𝑛𝑔\r\nCó thể bạn đã nạp một khối lượng lớn từ vựng nhưng một khi không áp dụng để nghe, nói, đọc, viết, dịch,\r\nlượng từ vựng đó sẽ trở thành TỪ VỰNG THỤ ĐỘNG. Việc bạn áp dụng từ đã học để nghe, nói, đọc, viết sẽ giúp\r\nbạn biết chúng thành TỪ VỰNG CHỦ ĐỘNG, nghĩa là chúng được sử dụng một cách linh hoạt hơn, với tần suất cao\r\nhơn.', NULL, NULL);
INSERT INTO `tips` VALUES (36981, 'Bộ thủ ( 疒 )\r\n- Từ chứa bộ thủ này thường có liên quan tới bệnh tật.\r\n- Ví dụ: 病，疯，疗... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36982, 'HSK 6 là cấp cao nhất của kỳ thi HSK. Đây là cấp bậc dành cho những người thành\r\nthạo tiếng Trung và có thể viết văn, luận văn bằng ngôn ngữ này. Đây cũng là mức khó nhất trong các cấp bậc\r\nHSK. Bài thi vẫn gồm 3 kỹ năng Nghe, Đọc hiểu và Viết trong thời gian 125 phút. Nghe 50 câu trong 35 phút,\r\nĐọc 50 câu trong 45 phút và Viết 1 câu trong 40 phút.\r\nThêm 5 phút để thí sinh điền thông tin và 10 phút cuối cùng để ghi đáp án vào giấy trả lời.\r\nĐây là mức độ đầu ra của bậc học Thạc Sĩ các trường đại học Trung Quốc. Nếu đạt được HSK cấp 6, bạn đã có\r\nthể sử dụng tiếng Trung thành thục gần bằng tiếng mẹ đẻ rồi.', NULL, NULL);
INSERT INTO `tips` VALUES (36983, 'Lục Thư là chỉ 6 cách sáng tạo ra chữ Hán. do người đời sau căn cứ vào sự hình\r\nthành của chữ Hán, chỉnh lý mà ra bao gồm có: Tượng Hình, Chỉ sự, Hình thanh, Hội ý, Giả tá, Chuyển chú.', NULL, NULL);
INSERT INTO `tips` VALUES (36984, 'Bộ thủ ( 亻 )\r\n- Từ chứa bộ thủ này thường có liên quan tới con người.\r\n- Ví dụ: 仁, 保 .......', NULL, NULL);
INSERT INTO `tips` VALUES (36985, 'Đọc thơ đoán từ vựng:\r\n\'Thân khuyển (犭) mà muốn thành vương (王)\r\nthế gian một chữ ngông CUỒNG đó em.\'\r\nĐây là chữ gì?', NULL, NULL);
INSERT INTO `tips` VALUES (36986, 'Kẹo hồ lô là một món rất quen thuộc mà khi nhắc đến ai cũng nghĩ ngay đến hình\r\ndáng viên kẹo tròn tròn nhỏ xinh có màu đỏ óng ánh, cùng với vị ngọt khó cưỡng. Nên được rất nhiều người ưa\r\nthích đặc biệt là trẻ em tại Trung Quốc.', NULL, NULL);
INSERT INTO `tips` VALUES (36987, 'Đọc câu đố đoán từ vựng:\r\n一个老头，不跑不走;请他睡觉，他就摇头\r\nYīgè lǎotóu, bù pǎo bù zǒu; qǐng tā shuìjiào, tā jiù yáotóu\r\n\'和\' chữ HÒA có năm cách đọc khác nhau gồm: hé、hè、huó、huò、hú.\r\nHanzii thử thách các bạn đọc đúng câu dưới đây:\r\n\'天气暖和(?)，我和(?)他和(?)面包饺子，他很讲原则，从不和(?)稀泥，也不会随声附和(?)别人，更不会做鼻帮别人和(?)牌。\'', NULL, NULL);
INSERT INTO `tips` VALUES (36988, 'Cách chào hỏi với người quen:\r\n- 没上课去呀？/打球去?/上班去?\r\n- 去哪儿了? 回来啦?\r\n- 吃了吗？', NULL, NULL);
INSERT INTO `tips` VALUES (36989, 'Phát âm rõ ràng, ngắt nghỉ đúng chỗ, lên xuống dựa theo câu.', NULL, NULL);
INSERT INTO `tips` VALUES (36990, 'Thư Pháp là nghệ thuật viết của chữ Hán. “Giáp cốt văn” thời nhà Ân, “Kim văn”\r\nthời nhà Chu, “Chữ triện” thời nhà Tần, “Lối chữ lệ” của thời nhà Hán, “Khải thư, hành thư, thảo thư” từ\r\nthời Đông Tấn đến thời nhà Đường, nghệ thuật thư pháp của Trung Hoa dần dần hoàn thiện, không ngừng phát\r\ntriển.', NULL, NULL);
INSERT INTO `tips` VALUES (36991, '很高兴认识谁\r\nĐể thể hiện sự vui vẻ khi lần đầu được gặp mặt/làm quen với ai, có thể nói theo cách sau:\r\nCấu trúc: “很高兴认识 + Tân ngữ ” hoặc “认识 + Tân ngữ+ Chủ ngữ + 很高兴”\r\nVí dụ:\r\n很高兴认识你。 (Hěn gāoxìng rènshi nǐ.)\r\nRất vui được biết bạn.\r\n认识你我很高兴。 (Rènshi nǐ wǒ hěn gāoxìng.)\r\nRất vui được làm quen với bạn. ', NULL, NULL);
INSERT INTO `tips` VALUES (36992, '“上有天堂, 下有苏杭” (Trên có thiên đàng; Dưới có Tô Hàng)\r\nCâu ngạn ngữ này xuất phát từ tác phẩm “Ngô Quận Chí” của Phạm Thành Đại đời Tống, nhằm mục đích chiêm\r\nngưỡng vẻ đẹp của Giang Nam, có thể sánh ngang với thiên đường. Câu nói về 2 địa danh đi vào sử sách thơ ca\r\nchính là Tô Châu và Hàng Châu - 2 địa điểm du lịch nổi tiếng bậc nhất của xứ Trung Hoa luôn đốn tim những\r\ntín đồ xê dịch bởi vẻ đẹp non nước hữu tình gắn liền với văn hoá là lịch sử ngàn năm.', NULL, NULL);
INSERT INTO `tips` VALUES (36993, 'Đọc thơ đoán từ vựng:\r\n“Chim chích mà đậu cành tre\r\nThập trên tứ dưới nhất đè chữ tâm”\r\nĐây là chữ gì?', NULL, NULL);
INSERT INTO `tips` VALUES (36994, 'Đọc câu đố đoán từ vựng:\r\n弟兄七八个，围着柱子坐，只要一分开，衣服就扯破。\r\nDìxiōng qībā gè, wéizhe zhùzi zuò, zhǐyào yī fēnkāi, yīfú jiù chě pò.', NULL, NULL);
INSERT INTO `tips` VALUES (36995, 'Hướng dẫn sử dụng: tìm KẾT HỢP TỪ：\r\n1. Tìm kiếm từ vựng.\r\n2. Ấn vào KẾT HỢP TỪ bên dưới từ vựng.', NULL, NULL);
INSERT INTO `tips` VALUES (36996, '爱没有年龄，没有界限； 永远不会消失。\r\nÀi méiyou niánlíng, méiyou jièxiàn; yǒngyuǎn bù huì xiāoshī.\r\nTình yêu không có tuổi tác, không có giới hạn; và không bao giờ lụi tàn.', NULL, NULL);
INSERT INTO `tips` VALUES (36997, 'Phương pháp Spaced Repetitionn - học từ vựng hiệu quả\r\nĐầu tiên, bạn cần chuẩn bị cho mình 1 tờ giấy A4, các loại bút highlight thường dùng (lưu ý: ưu tiên 3-5 màu\r\nkhác nhau, màu sáng và dễ nhìn).\r\n📍 Bước hai, bạn liệt kê cho mình các từ vựng cần học. Đó có thể là theo chủ đề hoặc theo sách. Theo chủ đề\r\nthì bạn soạn sẵn 1 list chủ đề và từ vựng của chủ đề đó. Học theo sách thì chuẩn bị sách trước và nhận định\r\nxem mình sẽ học từ vựng của unit nào trong sách.\r\nSau khi bạn đã thực hiện 2 bước trên xong thì mình bắt đầu \'chiến\' thôi 🔥\r\n(Các bước sau đây là mình tham khảo lại từ bài viết của B612 Planet)\r\n1️⃣ Bước 1: Gấp đôi tờ giấy A4 lại, viết chủ đề và 5 ngày học lên trên đầu. (Năm ngày này dựa theo quy tắc\r\n“Đường cong quên lãng Ebbinghaus”. Ngày đầu tiên là ngày bạn bắt đầu học từ mới, 4 ngày còn lại là ngày ôn\r\ntập từ mới bạn đã học và lần lượt cách ngày đầu tiên 1 ngày, 3 ngày, 6 ngày, 14 ngày.)\r\nVí dụ: Bạn bắt đầu học từ mới vào ngày 12/03 thì các ngày bạn ôn tập từ mới lần lượt là ngày 13/03, ngày\r\n15/03, ngày 18/03 và ngày 26/03.\r\nLưu ý: Dựa vào sách giáo khoa, tài liệu,... viết các từ cần ghi nhớ ra giấy theo cột dọc. Chỉ viết từ gốc\r\nchứ không viết nghĩa tiếng Việt.\r\n2️⃣ Bước 2: Sau khi đã ghi xong từ mới, bạn đọc một lượt những từ vừa được viết. Nếu bạn nhớ nghĩa của từ,\r\nđánh dấu ✔️, nếu không, hãy đánh dấu dấu ✖️.\r\n3️⃣ Bước 3: Sử dụng bút highlight để đánh dấu những từ bị đánh dấu ✖️ ở trên, mở sách và học lại nghĩa của\r\ncác từ đó.\r\n4️⃣ Bước 4: Sau khi học xong các từ mới của ngày hôm đó khoanh tròn vào ngày học ghi ở đầu trang.\r\n5️⃣ Bước 5: Nhiệm vụ của bạn những ngày sau đó là học unit mới + ôn lại unit đã học ở trước. Note lại tiếp\r\nlần hai những từ đã nhớ và chưa nhớ bằng ✔️ hoặc ✖️. Tiếp tục dùng bút highlight khác màu để đánh dấu ✖️\r\nnhững từ chưa nhớ và quay lại sách để tìm nghĩa của từ đó.', NULL, NULL);
INSERT INTO `tips` VALUES (36998, 'Bộ thủ ( 月)\r\n- Từ chứa bộ thủ này thường có liên quan tới thân thể, thịt, bộ phận con người hoặc thời\r\ngian.\r\n- Nếu bộ thủ này đứng phía bên trái sẽ biểu thị thân thể con người. Nếu đứng phía bên phải\r\nsẽ biểu thị liên quan tới thời gian.\r\n- Ví dụ:胆、肝、脸、朗、望... ...', NULL, NULL);
INSERT INTO `tips` VALUES (36999, 'Nghe nhắc lại đoạn văn trong HSK CAO CẤP\r\nNghe kết hợp với ghi chép (ghi ý, nếu được thì có thể ghi pinyin).', NULL, NULL);
INSERT INTO `tips` VALUES (37000, 'Bộ thủ ( 贝 )\r\n- Bộ bối trong giáp cốt văn chính là vẽ hình dạng của vỏ sò.\r\n- Thời xa xưa người ta thấy rằng vỏ sò thật đẹp, lại trân quý nên thường đem vỏ sò làm thành\r\nvòng đeo trên người. Cũng vì đó mà cổ nhân còn coi vỏ sò thành tiền tệ lưu hành.\r\n- Bởi lí do trên nên những Hán tự có chứa bộ Bối thường có quan hệ với tiền bạc. Ví dụ\r\n(赚、购、贫、货... ...)', NULL, NULL);
INSERT INTO `tips` VALUES (37001, 'Nếu đặt chân đến Trung Quốc mà bạn không thưởng thức được món vịt quay Bắc Kinh\r\nquả thật là điều vô cùng mất mát. Bởi món vịt quay Bắc Kinh là một niềm tự hào của nền ẩm thực Trung Quốc\r\nvới cách chế biến độc đáo, vịt có da vàng sẫm, thịt thấm đều gia vị chắc chắn sẽ khiến bạn không thể nào\r\nquên khi thưởng thức được mùi vị của món này đấy.', NULL, NULL);
INSERT INTO `tips` VALUES (37002, 'Phía sau tên nước, địa danh không được thêm 里 (lǐ).', NULL, NULL);
INSERT INTO `tips` VALUES (37003, 'Âm “h”: có khá nhiều bạn bị mắc khi đọc âm này. Chính xác nhất để phát âm âm này\r\nthì sẽ phải đọc giữa h và kh.', NULL, NULL);
INSERT INTO `tips` VALUES (37004, '不 （bù） mang nghĩa là \'Không, không phải \'.', NULL, NULL);
INSERT INTO `tips` VALUES (37005, 'Tứ đại gia tộc Trung Quốc:\r\nBốn gia tộc lớn gồm gia tộc Tương Trung Chính, gia tộc Tống Gia Chú, gia tộc Khổng Tường Hi và gia tộc Trần\r\nKỳ Mỹ.', NULL, NULL);
INSERT INTO `tips` VALUES (37006, 'Kết hợp đan xen 4 kỹ năng nghe nói đọc viết trong quá trình học\r\n\'社恐(shè kǒng)\' chỉ chứng sợ xã hội, sợ giao tiếp với môi trường mới.', NULL, NULL);
INSERT INTO `tips` VALUES (37007, 'Bộ thủ ( 讠 )\r\n- Từ chứa bộ thủ này thường có liên quan tới trò chuyện hoặc ngôn ngữ.\r\n- Ví dụ: 说、讲、话、语... ...', NULL, NULL);
INSERT INTO `tips` VALUES (37008, 'Lưu ý viết bài HSK6\r\n1. Nhớ viết tiêu đề (ngắn gọn, đắt)\r\n2. Lùi đầu dòng.\r\n3. Đọc lại bài trước khi nộp.', NULL, NULL);
INSERT INTO `tips` VALUES (37009, '\'大震惊\' ngôn ngữ mạng \'shock nặng\'', NULL, NULL);
INSERT INTO `tips` VALUES (37010, '已经 hay 已 thường kết hợp với 了.\r\nVí dụ: 妈妈已经回来了 (māmā yǐjīng huílái le) Mẹ đã về rồi', NULL, NULL);
INSERT INTO `tips` VALUES (37011, 'Chú ý ngữ khí câu.', NULL, NULL);
INSERT INTO `tips` VALUES (37012, 'Theo Wikipedia, phép dùng bộ thủ xuất hiện từ thời nhà Hán trong bộ sách Thuyết\r\nvăn giải tự (説文解字) của tác giả Hứa Thuận. Bộ sách này hoàn tất vào năm 121 đã liệt kê hơn 9000 chữ Hán và\r\nsắp xếp chúng thành 540 nhóm khác nhau, tức là 540 bộ thủ nguyên thủy.', NULL, NULL);
INSERT INTO `tips` VALUES (37013, 'Thanh 1 có kí hiệu trong bính âm (pinyin) là một dấu gạch ngang “-” . Khi phát\r\nâm sẽ có trường độ dài.', NULL, NULL);
INSERT INTO `tips` VALUES (37014, 'Tìm một người bạn đồng hành.', NULL, NULL);
INSERT INTO `tips` VALUES (37015, 'Phương pháp mở rộng là gì?\r\nPhương pháp mở rộng tức là thêm các thành phần câu, khiến nghĩa câu càng cụ thể, sinh động,\r\nmang tính hình tượng.\r\nCó thể nói giống như khi ta vẽ cây vậy, vẽ xong nhánh chính sẽ có nhánh phụ, thêm vài cái\r\nlá, cái nụ.\r\nVí dụ câu đơn giản. 小鸟唱歌.\r\nCó thể mở rộng như này.\r\nThêm lượng từ：一只小鸟唱歌\r\nThêm tính từ:：一只漂亮的小鸟唱歌\r\nThêm thời gian：春雨过后，太阳出来了。一只漂亮的小鸟在唱歌\r\nThêm địa điểm：春雨过后，太阳出来了。一只漂亮的小鸟在树上唱歌\r\nThêm biện pháp tu từ：春雨过后，太阳出来了。一只漂亮的小鸟在树上欢快地唱歌\r\nThêm cảm nhận：春雨过后，太阳出来了。一只漂亮的小鸟在树上欢快地唱歌，感觉。。。', NULL, NULL);
INSERT INTO `tips` VALUES (37016, 'Trong câu chữ “比” không sử dụng các phó từ biểu thị mức độ cao như “很”.', NULL, NULL);
INSERT INTO `tips` VALUES (37017, 'Hướng dẫn sử dụng: tìm NGỮ PHÁP：\r\n1. Ấn tới mục NGỮ PHÁP.\r\n2. Nhập từ vựng hoặc mục ngữ pháp cần tìm.', NULL, NULL);
INSERT INTO `tips` VALUES (37018, 'Dùng HANZII lâu nhưng có thể bạn chưa biết?\r\nĐể tìm mẹo chữ hán, chúng ta làm theo hai bước:\r\n1. Tra cứu từ vựng.\r\n2. Ấn sang phần \'Hán tự\'\r\nTrong phần hán tự sẽ có phần tập viết và Mẹo Hán tự bên dưới.', NULL, NULL);
INSERT INTO `tips` VALUES (37019, 'Chiến lược thi bài miêu tả tranh:\r\n1. Hiểu và nhớ kỹ mục tiêu và điểm chính của bài miêu tả tranh.\r\n2. Quan sát tỉ mỉ, chắt lọc thông tin, chú trọng chi tiết đặc biệt trong tranh.\r\n3. Dựa vào chi tiết đặc thù, bổ sung tình tiết, hoàn thành khung câu chuyện.\r\n4. Sử dụng thời gian hợp lý, viết ngắn gọn ra nháp.', NULL, NULL);
INSERT INTO `tips` VALUES (37020, 'Hãy dành thời gian đọc thật nhiều sách để tăng vốn từ vựng và khả năng nhận diện\r\nmặt chữ nhé!', NULL, NULL);
INSERT INTO `tips` VALUES (37021, 'Từ ngữ sử dụng không phù hợp\r\nLoại lỗi sai này nhằm kiểm tra thí sinh có biết và hiểu rõ cách dùng của từ ngữ hay không. Nguyên nhân xảy\r\nra lỗi sai này có rất nhiều ví dụ như:\r\n- Sử dụng giới từ không phù hợp\r\n- Sử dụng liên từ không phù hợp\r\n- Biểu thị số lượng giảm thì không được dùng bội số.', NULL, NULL);
INSERT INTO `tips` VALUES (37022, 'Bộ thủ ( 纟)\r\n- Từ chứa bộ thủ này thường có liên quan tới hàng dệt may hoặc màu sắc.\r\n- Ví dụ: 线、纸、红、涂... ...', NULL, NULL);
INSERT INTO `tips` VALUES (37023, 'Trong đám cưới của người Trung Quốc, bố mẹ chú rể thường chuẩn bị trong phòng\r\ntân hôn của cô dâu chú rể một túi đỏ bên trong có 4 loại củ quả là táo tàu, hạt sen, quả nhãn và hạt lạc.\r\nTrong tiếng Trung Quốc, các loại củ quả trên có cách phát âm lần lượt như sau:\r\n– Quả táo tầu (táo đỏ) – 红枣 (Hóngzǎo)\r\n– Hạt lạc – 花生 (Huāshēng)\r\n– Quả nhãn – 桂圆 (Guìyuán)\r\n– Hạt sen – 莲子 (Liánzǐ)\r\nKhi ghép 4 loại này vào sẽ có câu: “枣生桂子” (Zǎo shēng guìzǐ), cách đọc này cũng đồng thời là cách đọc của câu\r\nchúc rất hay: “早生贵子” (TẢO SINH QUÝ TỬ), chúc tân lang tân nương sớm có con trai nối dõi.', NULL, NULL);
INSERT INTO `tips` VALUES (37024, 'Học các từ vựng thông qua bộ thủ và các chữ trùng nhau.', NULL, NULL);
INSERT INTO `tips` VALUES (37025, 'Hình thanh là lấy sự làm tên, mượn thanh để hợp thành. Đây là phép thông dụng\r\nnhất để hình thành Hán tự. Chữ hài thanh gồm một phần chỉ nghĩa, một phần chỉ thanh.\r\nVí dụ:沐 Mộc; 郡 Quận; 芳 Phương; 婆 Bà; 固 Cố;問 (问) Vấn ; 辯 (辩) Biện', NULL, NULL);
INSERT INTO `tips` VALUES (37026, 'Bộ thủ ( 阝)\r\n- Thời cổ đại tại Trung Quốc, thường sẽ mời những người tôn quý ngồi bên trái. Bởi vậy ta có\r\nthể bắt gặp bộ thủ này thường phía bên tay trái của Hán tự.\r\n- Bộ thủ này từ sớm đã được dùng để biểu thị núi đồi. Bởi vậy đa số các từ có chứa nó\r\nđều có liên quan tới núi non, địa hình. Ví dụ một ngọn núi đứng chắn trước mặt bạn sẽ tạo\r\nthành (阻碍、障碍), núi thì thường (危险)......', NULL, NULL);
INSERT INTO `tips` VALUES (37027, '这几天天天天气不好。\r\nBạn đoán được ý nghĩa câu này không?', NULL, NULL);
INSERT INTO `tips` VALUES (37028, 'Cách chào hỏi:\r\n- 你好!\r\n- Danh xưng + 好\r\n- 早、您早、早上好', NULL, NULL);
INSERT INTO `tips` VALUES (37029, '\'穷屌丝\' ngôn ngữ mạng \'đỗ nghèo khỉ\'', NULL, NULL);
INSERT INTO `tips` VALUES (37030, '万物皆有美，但不是每个人都能看到。\r\nWànwù jiē yǒu měi, dàn bú shì měi gèrén dōu néng kàn dào.\r\nMọi thứ đều có vẻ đẹp, nhưng không phải ai cũng nhìn thấy điều đó.', NULL, NULL);
INSERT INTO `tips` VALUES (37031, 'Với bài thi HSKK các cấp độ, thí sinh đạt từ 60 điểm là đỗ.\r\n\'颜控\' ngôn ngữ mạng \'mê cái đẹp\'', NULL, NULL);
INSERT INTO `tips` VALUES (37032, 'Đánh giá, cho điểm thí sinh dựa theo tiêu chí dưới đây:\r\n- Thi sinh sẽ đạt điểm số cao khi có thể nhắc lại chính xác câu vừa được nghe.\r\n- Thí sinh sẽ đạt điểm số trung bình khi không thể nhắc lại hoàn chỉnh câu vừa được nghe.\r\n- Thí sinh sẽ đạt điểm số thấp khi nội dung nhắc lại của thí sinh ít liên quan đến nội dung\r\ncâu vừa được nghe.', NULL, NULL);
INSERT INTO `tips` VALUES (37033, 'Học tiếng Trung qua podcast\r\nBạn sẽ tìm thấy rất nhiều Podcast tiếng Trung thú vị trên Internet kèm transcript giúp bạn luyện nghe. Sau\r\nđó, hãy viết lại ra giấy những gì mình nghe được (听后感）', NULL, NULL);
INSERT INTO `tips` VALUES (37034, '想 (xiǎng) còn có nghĩa là \'nhớ, nhung nhớ\'\r\nVí dụ: 我想你 (Wǒ xiǎng nǐ) Em nhớ anh', NULL, NULL);
INSERT INTO `tips` VALUES (37035, 'Tứ đại kỳ thư Trung Quốc:\r\nTam Quốc diễn nghĩa của La Quán Trung\r\nThủy hử của Thi Nại Am\r\nTây du ký của Ngô Thừa Ân\r\nKim Bình Mai của Lan Lăng Tiếu Tiếu Sinh', NULL, NULL);
INSERT INTO `tips` VALUES (37036, 'Mỗi ngày hãy dành ra 20 - 30 phút để luyện nghe tiếng Trung, bạn có thể mở bài\r\nkhóa, radio hay bất kỳ chương trình nào mà mình yêu thích.', NULL, NULL);
INSERT INTO `tips` VALUES (37037, '\'浇花浇根(jiāo huā jiāo gēn)\' Chân thành đổi lấy chân thành.', NULL, NULL);
INSERT INTO `tips` VALUES (37038, 'Để nói quốc tịch của một người chúng ta có cấu trúc: Tên nước + 人 (rén).', NULL, NULL);
INSERT INTO `tips` VALUES (37039, 'Nếu muốn mô tả qua một cái gì nào đó để làm gì thì Đài Loan sẽ\r\ndùng từ \'透过\' , còn Trung Quốc dùng từ \'通过\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37040, 'Lỗi thiếu logic trong HSK6\r\n“Thiếu logic” dùng để chỉ các câu có ngữ pháp chuẩn nhưng ý nghĩa câu lại không có logic. Những kiểu câu như\r\nvậy thường dùng để kiểm tra khả năng phân tích, đọc hiểu của thí sinh. Câu “thiếu logic” bao gồm những kiểu\r\ncâu như: thứ tự logic không phù hợp, logic trước sau bị mâu thuẫn và chính phụ bị đảo lộn.', NULL, NULL);
INSERT INTO `tips` VALUES (37041, 'Cách phát hiện lỗi sai:\r\n- Thứ tự logic không phù hợp là lỗi sai vô cùng phổ biến.\r\n- Về nội dung, thứ tự logic đúng phải là từ trước tới sau (thời gian), từ hẹp đến rộng (không gian), từ thấp\r\ntới cao (trình độ).\r\n- Về cấu trúc, thứ tự logic thường được thể hiện dưới các dạng như câu liệt kê, câu phức. Chính vì thế, khi\r\ngặp phải những ngữ đoạn liệt kê hoặc câu phức, thí sinh cần phải chú ý đến thứ tự logic giữa các phần nhỏ\r\ntrong câu.', NULL, NULL);
INSERT INTO `tips` VALUES (37042, 'Tứ đại phát minh của Trung Quốc bao gồm giấy, kỹ thuật in, thuốc súng và la bàn.', NULL, NULL);
INSERT INTO `tips` VALUES (37043, 'Động từ 是 (shì) không bao giờ biến đổi theo chủ ngữ.', NULL, NULL);
INSERT INTO `tips` VALUES (37044, 'Hoa cúc mặc dù nằm trong 4 loại hoa quý của Trung Quốc, là biểu tượng của giới\r\nquý tộc thì cũng không thể trở thành quà tặng. Nó mang hàm ý của sự đau buồn và cũng là loài hoa thường được\r\nmang theo khi viếng mộ.', NULL, NULL);
INSERT INTO `tips` VALUES (37045, 'Bộ thủ ( 忄 )\r\n- Từ chứa bộ thủ này thường có liên quan tới hoạt động tâm lý hoặc tư tưởng.\r\n- Ví dụ: 恨、慌、怪... ...', NULL, NULL);
INSERT INTO `tips` VALUES (37046, 'Hãy thực hành nói những tính từ liên quan đến vị giác đã học trong khi nấu ăn,\r\năn uống. Kết hợp giữa học lý thuyết và cảm nhận thực tế.', NULL, NULL);
INSERT INTO `tips` VALUES (37047, 'Tứ đại danh tác Trung Quốc gồm:\r\nTam Quốc diễn nghĩa (三国演义) của La Quán Trung\r\nThủy hử (水浒传) của Thi Nại Am\r\nTây du ký (西游记) của Ngô Thừa Ân\r\nHồng lâu mộng (红楼梦) của Tào Tuyết Cần', NULL, NULL);
INSERT INTO `tips` VALUES (37048, 'Trong khi nghe phần một, thí sinh cần cố gắng nhớ thông tin chứ đừng\r\nđơn thuần nhớ từ vựng.', NULL, NULL);
INSERT INTO `tips` VALUES (37049, 'Thi HSK4 tổng điểm trên 180 điểm là bạn đã đỗ &lt;3', NULL, NULL);
INSERT INTO `tips` VALUES (37050, 'ở Đài Loan và một số tỉnh TQ \'窝心\' nghĩa là trong lòng cảm thấy “ấm áp”,\r\nnhưng ở miền Bắc Trung Quốc thường biểu thị “uất ức、đau khổ trong lòng”, vì từ này dễ gây hiểu nhầm và các\r\ntỉnh Trung Quốc đều hiểu khác nhau nên hiện nay thường sử dụng từ “暖心” để biểu thị trong lòng cảm thấy ấm\r\náp.', NULL, NULL);
INSERT INTO `tips` VALUES (37051, '“摆烂(bǎi làn)” chỉ sự việc không thể xấu hơn được nữa, bản thân mình sẽ \'mặc kệ\',\r\n\'đến đâu thì đến\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37052, 'Người Trung Quốc kiêng tặng gương vì một là gương dễ vỡ, gương vỡ thường là điềm\r\nbáo mối quan hệ tan vỡ. Lý do khác là liên quan đến văn hóa phong thủy cũ của Trung Quốc. Gương là một công\r\ncụ quan trọng để thay đổi bố cục phong thủy của một ngôi nhà, vì vậy nếu đặt gương sai hướng, điều xui xẻo\r\nsẽ ập đến.', NULL, NULL);
INSERT INTO `tips` VALUES (37053, 'Đánh giá về khả năng miêu tả chi tiết, khả năng liên tưởng của thí\r\nsinh.\r\n\'细节控\' ngôn ngữ mạng \'để ý chi tiết\'', NULL, NULL);
INSERT INTO `tips` VALUES (37054, 'Kinh nghiệp luyện đọc tiếng Trung thi HSK\r\nKhi đọc tiếng Trung, bắt buộc bạn phải có vốn từ vựng phong phú. Đối với bài thi HSK, bạn cần phải nắm được\r\nsố lượng từ vựng tương ứng với cấp độ đăng ký thi. Vậy nên việc trau dồi từ vựng là vô cùng quan trọng.\r\nViệc chọn tài liệu đọc cũng rất quan trọng. Bạn nên đọc những chủ đề mà mình quan tâm để có hứng thú khi\r\nđọc. Chỉ cần bỏ ra mỗi ngày từ 15 – 20 phút là bạn có thể cải thiện khả năng đọc đáng kể rồi. Thêm vào đó,\r\nbạn cũng nên tập cách đọc lướt, nắm ý chính. Điều này sẽ giúp ích rất nhiều trong quá trình làm bài thi HSK.\r\nVì khi làm bài, bạn không có quá nhiều thời gian để đọc kỹ từng từ một. Cần phải đọc lướt để tìm ra nội dung\r\nthích hợp cho câu trả lời. Có như vậy, bạn mới có khả năng đạt điểm cao được.\r\nHãy đọc đúng, đọc đủ, và đọc có chọn lọc.', NULL, NULL);
INSERT INTO `tips` VALUES (37055, 'Trong số năm sáu ngàn chữ Kim văn đã thu thập được, ta có thể đọc và\r\ngiải thích được phần lớn.', NULL, NULL);
INSERT INTO `tips` VALUES (37056, 'Học từ vựng theo chủ đề\r\nKhi được nhóm vào cùng 1 chủ đề, việc học thuộc từ vựng đảm bảo sẽ trở nên đơn giản hơn rất nhiều.\r\nĐây là cách đặc biệt hay cho người làm việc văn phòng vì khi học từ vựng theo chuyên ngành mình làm, bạn sẽ\r\nphát huy được tối đa vốn từ vựng đó, khiến cuộc hội thoại của bạn với đồng nghiệp hay đối tác nước ngoài\r\ntrôi chảy và nhiều ý tưởng.', NULL, NULL);
INSERT INTO `tips` VALUES (37057, 'Tự tạo ra môi trường tiếng Trung\r\nBạn nên thực hành những kiến thức đã học bằng cách nói tiếng Trung thường xuyên. Chẳng hạn như khi đi mua\r\nsắm, hãy thử gọi tên các món đồ mình muốn mua bằng tiếng Trung, ghi chú lại những từ mình chưa biết để tra\r\ntừ điển và học thêm.', NULL, NULL);
INSERT INTO `tips` VALUES (37058, 'Khi trao đổi với sếp hay đồng nghiệp nước ngoài, ngay cả khi vốn từ còn hạn chế hay ngữ pháp chưa vững, bạn\r\nhãy cố gắng diễn đạt ý muốn nói bằng những từ vựng đơn giản nhất có thể.', NULL, NULL);
INSERT INTO `tips` VALUES (37059, 'Ngoài ra, bạn có thể nhờ một người bạn giỏi tiếng Trung chỉnh sửa, góp ý cho mình để dần dần khắc phục các\r\nnhược điểm. Hãy cho bản thân được phép sai vì tiếng Trung vốn không phải tiếng mẹ đẻ, bạn có quyền tự tin vì\r\nmình có khả năng giao tiếp bằng hai ngôn ngữ khác nhau.', NULL, NULL);
INSERT INTO `tips` VALUES (37060, '214 bộ thủ chữ Hán có chức năng dễ nhận diện nhất đó là giúp phân chia các loại\r\nchữ Hán. Căn cứ vào các bộ thủ tiếng Trung, việc biên soạn từ điển cũng sẽ có quy củ hơn. 214 bộ thủ còn có\r\ncông dụng biểu nghĩa tuy không hẳn chính xác nhưng giúp người đọc có thể suy ra nghĩa gốc.', NULL, NULL);
INSERT INTO `tips` VALUES (37061, 'Bộ thủ ( 门 )\r\n- Từ chứa bộ thủ này thường có liên quan tới phòng ốc, cửa ra vào.\r\n- Ví dụ: 问、间... ...', NULL, NULL);
INSERT INTO `tips` VALUES (37062, '如果你不能做大事，那就以伟大的方式做小事。\r\nRúguǒ nǐ bù néng zuò dàshì, nà jiù yǐ wěidà de fāngshì zuò xiǎoshì.\r\nNếu bạn không thể làm những điều vĩ đại, vậy hãy làm những việc nhỏ bé theo cách vĩ đại.', NULL, NULL);
INSERT INTO `tips` VALUES (37063, '\'上头\' ngôn ngữ mạng \'mêeeee\'', NULL, NULL);
INSERT INTO `tips` VALUES (37064, 'Bánh bao kim sa là một món đặc sản nổi tiếng tại Trung Quốc và được rất nhiều\r\nngười ưa chuộng bởi chiếc bánh mềm xốp nhân bên trong sền sệt có vị mằn mặn ngọt ngọt của trứng muối và sữa\r\nđặc,...Mùi vị không thể lẫn vào đâu được.', NULL, NULL);
INSERT INTO `tips` VALUES (37065, 'Món sủi cảo cũng được xem là niềm tự hào của nền ẩm thực Trung Quốc được làm từ\r\nnguyên liệu là bột mì, tôm, cá và các loại rau củ khác tất cả tạo nên một hương vị vô cùng khó cưỡng.', NULL, NULL);
INSERT INTO `tips` VALUES (37066, 'Bộ thủ ( 冂 )\r\n- Từ chứa bộ thủ này thường có liên quan tới mối quan hệ giữa các sự vật hoặc phạm vi của\r\nchúng.\r\n- Ví dụ: 同，网...', NULL, NULL);
INSERT INTO `tips` VALUES (37067, 'Đọc câu đố đoán từ vựng:\r\n两只小口袋，天天随身带，要是少一只，就把人笑坏。\r\nLiǎng zhī xiǎo kǒudài, tiāntiān suíshēn dài, yàoshi shǎo yī zhǐ, jiù bǎ rén xiào huài.\r\n\'晕\' chữ VỰNG có hai cách đọc khác nhau gồm: yūn、yùn.\r\nHanzii thử thách các bạn đọc đúng câu dưới đây:\r\n\'看见日晕(?)她就头晕(?)眼花\'', NULL, NULL);
INSERT INTO `tips` VALUES (37068, 'Bài thi HSK 5 dành cho những người có trình độ tiếng Trung cao cấp. Nghĩa là bạn\r\ncó thể xem phim, tạp chí Trung Quốc và có khả năng viết những đoạn văn dài bằng tiếng Trung. Bài thi HSK cấp\r\nđộ 5 kéo dài đến 125 phút với 3 phần thi. Phần nghe dài 30 phút, đọc hiểu 40 phút và viết 40 phút.\r\nBạn vẫn sẽ có 5 phút để điền thông tin cá nhân và 10 phút điền vào tờ giấy trả lời. Để đạt được HSK cấp độ\r\n5, bạn cần phải biết ít nhất 2.500 từ tiếng Trung. Trong số đó có khoảng 1.300 từ không thông dụng.', NULL, NULL);
INSERT INTO `tips` VALUES (37069, 'Tại đất nước Trung Quốc, hoa mẫu đơn được coi là vua của các loài hoa bởi vì nó\r\nđại diện cho sự vương giả cùng với vẻ đẹp thanh cao và quý phái. Ngày xưa quốc hoa Trung Quốc thường được sử\r\ndụng rất nhiều trong cung cấm còn ngày nay thì trở thành một trong những loài hoa được yêu thích bậc nhất\r\ntrong giới chơi hoa.', NULL, NULL);
INSERT INTO `tips` VALUES (37070, 'Mỳ trường thọ có tên gọi là \'长寿面\' ý muốn chúc người ăn sống lâu trăm tuổi sức\r\nkhỏe dồi dào. Thường được ăn trong dịp sinh nhật.\r\n\'涨\' chữ TRƯỚNG có hai cách đọc khác nhau gồm: zhǎng、zhàng.\r\nHanzii thử thách các bạn đọc đúng câu dưới đây:\r\n\'房子涨(?)价涨的他头昏脑涨(?)\'', NULL, NULL);
INSERT INTO `tips` VALUES (37071, 'Bộ thủ ( 氵 )\r\n- Từ chứa bộ thủ này thường có liên quan tới nước.\r\n- Ví dụ: 江、湖、海、温、渴... ...', NULL, NULL);
INSERT INTO `tips` VALUES (37072, 'Khi tập phát âm những âm bật hơi, hãy sử dụng giấy, giấy ăn đưa ra trước mặt và\r\ntập đọc.', NULL, NULL);
INSERT INTO `tips` VALUES (37073, '你可以拖延，但时间不会。\r\nNǐ kěyǐ tuōyán, dàn shíjiān bú huì.\r\nBạn có thể trì hoãn, nhưng thời gian thì không.', NULL, NULL);
INSERT INTO `tips` VALUES (37074, 'Cần cù bù thông minh.', NULL, NULL);
INSERT INTO `tips` VALUES (37075, 'Quy tắc giữa trước hai bên sau:\r\nNhững chữ có ba bộ phận đối xứng , thì ưu tiên viết bộ phận ở giữa, sau đó sẽ viết bên trái rồi đến bên phải\r\n. Chữ đặc trưng là chữ 水', NULL, NULL);
INSERT INTO `tips` VALUES (37076, 'So sánh về THI MÁY và THI VIẾT\r\nThi máy: Với cách thức trả lời, bạn chỉ cần ấn tích và chọn đáp án trực tiếp trên máy tính.\r\nThi giấy: Bạn sẽ phải tô và viết đáp án trên bài thi.', NULL, NULL);
INSERT INTO `tips` VALUES (37077, 'Học ngành tiếng Trung dễ hay khó?', NULL, NULL);
INSERT INTO `tips` VALUES (37078, 'Khả̉i thư nét chữ ngay ngắn, đáng được coi là chuẩn mực nên được gọi\r\nlà khải thư, \'Chính thư\' hoặc \'Chân thư\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37079, 'Cờ vây là do “Nghiêu Đế”, một trong “Ngũ đế” phát minh ra, đến nay đã có 4000\r\nnăm lịch sử. Đặc điểm của loại cờ này là hai bên quân cờ trắng và đen luôn công kích và bao vây lẫn nhau nên\r\n“đánh cờ” được gọi là “cờ vây”.', NULL, NULL);
INSERT INTO `tips` VALUES (37080, 'Đại đa số các chữ trong tiếng Trung đều có cấu tạo theo kiểu chữ hình thanh, tức\r\nmột chữ Hán sẽ có phần hình (phần thể hiện ý nghĩa của chữ) và phần thanh (phần thể hiện âm đọc của chữ).\r\nPhần hình, còn gọi là hình bàng (形旁); phần thanh, còn gọi là thanh bàng (声旁). Hình bàng và thanh bàng được\r\ngọi chung là thiên bàng (偏旁).', NULL, NULL);
INSERT INTO `tips` VALUES (37081, '“地道”với “道地”đều có thể hiểu là “chính cống, thuần tuý” cách dùng như nhau, người\r\nĐài Loan chỉ dùng từ \'道地\', còn người Trung Quốc thường nói \'地道\' mà ít nói \'道地\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37082, '𝐺ℎ𝑖 𝑎̂𝑚 𝑣𝑎̀ 𝑡𝑢̛̣ đ𝑎́𝑛ℎ 𝑔𝑖𝑎́ 𝑛ℎ𝑎̣̂𝑛 𝑥𝑒́𝑡\r\nBạn có thể bắt đầu với các bài khoá trong SGK, thường thì các bài khoá đều có file nghe đi kèm, hãy mở file\r\nnghe trước, sau đó đọc và ghi âm lại. Bạn sẽ phải đối diện với khuyết điểm của mình, nghe lại và note lại\r\nnhững lỗi sai của bạn, hoặc có thể nghe đối chiếu giữa bản thân và người ta đọc xem có gì chưa đạt（đọc sai\r\nâm, sai trọng âm, thiếu ngữ điệu,…）', NULL, NULL);
INSERT INTO `tips` VALUES (37083, 'Dựa vào tranh chúng ta cũng có thể quan sát dựa theo:\r\n1. Nhân vật\r\n2. Sự kiện\r\n3. Hoàn cảnh\r\n4. Thời gian\r\n5. Thần sắc, phong thái nhân vật\r\n6. Đạo cụ liên quan', NULL, NULL);
INSERT INTO `tips` VALUES (37084, 'Hướng dẫn sử dụng: tìm SỔ TAY CAO CẤP：\r\n1. Ấn tới mục sổ tay.\r\n2. Kéo xuống mục CAO CẤP.\r\n\'这山望着那山高(zhè shān wàng zhe nà shān gāo)\' Đứng núi này trông núi nọ\r\nTrong tiếng Trung cái ô phát âm đồng nghĩa với từ \'chia xa\' nên chắc chắn không\r\nphải món quà hợp lý để tặng trong ngày kỷ niệm hay lễ Valentine.\r\n\'审丑(shěn chǒu)\' chỉ từ vựng ngược nghĩa với thẩm mỹ (审美). Mang nghĩa gu thẩm mỹ\r\ncó vấn đề, thích những thứ kỳ lạ, xấu xí. Đi ngược với thẩm mỹ của mọi người.', NULL, NULL);
INSERT INTO `tips` VALUES (37085, 'Người Trung Quốc kiêng tặng đồng hồ vì cách phát âm tiếng Trung của ‘đồng hồ’ (钟\r\nzhōng) giống với ‘kết thúc’ (终 zhōng). Gợi ý về cái chết , kết thúc của một đời người. Vì vậy việc tặng đồng\r\nhồ như một món quà Tết Nguyên Đán là điều cấm kỵ.', NULL, NULL);
INSERT INTO `tips` VALUES (37086, 'Vốn được dùng để chỉ những học sinh nhỏ tuổi xuất thân từ một gia đình bình\r\nthường ở các thành phố hạng ba và hạng bốn. Chăm chỉ, đạt thành tích cao trong kỳ thi tuyển sinh đại học\r\nnhưng không có hậu thuẫn. 小镇做题家” thường không quá nổi bật, điều nổi bật duy nhất là kĩ năng giải đề trên\r\ntrường, nhưng sau khi vào đại học thì không còn những người như vậy.', NULL, NULL);
INSERT INTO `tips` VALUES (37087, 'Đây là từ lóng có nguồn gốc từ group “985废物引进计划” trên Douban được cư dân mạng sử dụng để tự chế giễu bản\r\nthân một cách bất lực. Tuy nhiên, từ lóng này cũng không quá tiêu cực, mọi người vẫn dùng để giới thiệu bản\r\nthân.\r\n\'热干面 (rè gān miàn)\' Món ăn sáng đặc biệt ở Hồ Bắc, là một trong số loại mì nổi\r\ntiếng nhất Trung Quốc.', NULL, NULL);
INSERT INTO `tips` VALUES (37088, 'Tứ đại danh trà Trung Quốc:\r\nTrà Long Tỉnh (Chiết Giang)\r\nTrà Bích Loa Xuân (Giang Tô)\r\nTrà Thiết Quan Âm (Phúc Kiến)\r\nTrà Mao Phong (An Huy)', NULL, NULL);
INSERT INTO `tips` VALUES (37089, 'Những món ăn Tứ Xuyên lôi cuốn thực khách bằng hương vị cay nồng, đậm vị bởi ớt,\r\nhoa tiêu và hạt tiêu. Có câu nói: “thực tại Trung Quốc, vị tại Tứ Xuyên” đủ để thấy sức ảnh hưởng của trường\r\nphái ẩm thực Tứ Xuyên ở Trung Quốc. Ở bất cứ đâu tại Trung Quốc, thực khách cũng có thể tìm thấy hương vị\r\ncủa món ăn Tứ Xuyên trong các món ăn của khu vực đó. Ớt ở Tứ Xuyên được sử dụng rất linh hoạt có khi nó là\r\nnguyên liệu chính và có khi làm nguyên liệu bổ trợ và thường được dùng nhất với vai trò gia vị. Ngoài sự\r\nlinh hoạt trong vai trò, những món ăn Tứ Xuyên còn sử dụng ớt một cách linh hoạt trong liều lượng và điều\r\nchỉnh tùy theo mùa hoặc tùy theo khẩu vị thực khách. Món ăn Tứ Xuyên không những lắm vị và nồng hậu,\r\nmà còn có sở trường về mặt thanh, tươi, đạm, nhã, khiến người ăn đều tấm tắc khen ngợi và\r\nthật khó quên. Các món đặc trưng của trường phái ẩm thực Tứ Xuyên: vây cá kho khô, cua xào thơm cay.', NULL, NULL);
INSERT INTO `tips` VALUES (37090, 'Định ngữ gồm:\r\n- Định ngữ loại 1: thường là danh từ, ngữ danh từ, tính từ, ngữ tính từ, động từ, ngữ động từ, đại từ, số\r\nlượng từ, từ tượng thanh,…\r\n- Định ngữ loại 2: ngữ liên hợp, ngữ chính phụ, ngữ động tân, ngữ chủ vị, ngữ giới tân, ngữ phương vị,\r\nngữ so sánh, các loại bổ ngữ,…', NULL, NULL);
INSERT INTO `tips` VALUES (37091, 'Lỗi sai khi sử dụng giới từ\r\nGiới từ thường đi kèm với danh từ chỉ người hoặc cụm danh từ để tạo thành một kết cấu giới từ. Kết cầu giới\r\ntừ thường xuyên được chọn để thiết kế các lỗi sai, thí sinh cần chú ý phần này.', NULL, NULL);
INSERT INTO `tips` VALUES (37092, 'Cách nhận biết lỗi sai:\r\n- Người ra đề thường đưa ra các lỗi sai như: Kết cấu giới từ khiến câu mất chủ ngữ; câu không có giới từ;\r\nchủ thể, khách thể bị đảo lộn và giới từ sử dụng không đúng. Do vậy, khi nhìn thấy đề bài có kết cấu giới\r\ntừ, trước hết chúng ta cần phải xác định được các thành phần chính của câu xem có bị thiếu hay không, tiếp\r\ntheo cần xác định chủ thể và khách thể, dựa vào đó để xác định chủ thể và khách thể có bị đảo lộn hay không,\r\ncuối cùng cần kiểm tra giới từ đã sử dụng đúng hay chưa.', NULL, NULL);
INSERT INTO `tips` VALUES (37093, 'Kỳ thi được quản lý bởi Hanban là cơ quan thuộc Bộ giáo dục Trung Quốc và do\r\nTrung tâm kiểm tra trình độ tiếng Hán đại học ngôn ngữ Bắc Kinh thiết kế ra.\r\nHSK được tổ chức hàng năm tại Trung Quốc và Nước ngoài, nơi mà đáp ứng được các yêu cầu về tiêu chuẩn và\r\nngười thi sẽ nhận được Chứng chỉ trình độ tiếng Hán (汉语水平证书）tương ứng.\r\nBộ Giáo dục Trung Quốc đã thành lập Ủy ban Thẩm định Khả năng Trung Quốc Quốc gia, có toàn quyền chỉ đạo\r\ncuộc thi HSK, bao gồm cả việc cấp Chứng chỉ HSK.\r\nTính đến năm 2014, thế giới đã có tổng cộng 860 trung tâm thi HSK, trong đó 530 điểm kiểm tra ở nước ngoài,\r\nnằm ở 112 quốc gia. Trung Quốc có 330 điểm thi, phân bố ở 71 tỉnh thành.', NULL, NULL);
INSERT INTO `tips` VALUES (37094, '\'气氛担当\' ngôn ngữ mạng \'khuấy động bầu không khí\'', NULL, NULL);
INSERT INTO `tips` VALUES (37095, 'HSK cấp độ 4 vẫn gồm có 3 phần thi là: Nghe, Đọc hiểu và Viết. Thời gian thi kéo\r\ndài trong 90 phút. Phần nghe có 45 câu thi trong 30 phút. Phần đọc gồm 40 câu thi trong 35 phút. Cuối cùng\r\nlà phần viết 15 câu trong 25 phút. Bạn vẫn sẽ có thêm 5 phút điền thông tin và 10 phút để chép đáp án vào\r\ngiấy trả lời.\r\nThông thường khi đi du học Trung Quốc, nếu bạn đạt được HSK 4 thì sẽ được miễn thi đầu vào. Nếu có chứng chỉ\r\nHSK 4 loại ưu tú thì bạn sẽ có nhiều cơ hội để được nhận học bổng với giá trị lớn.\r\nĐạt được cấp độ này đồng nghĩa với việc bạn đã có thể giao tiếp tốt bằng tiếng Trung để có thể học tập, du\r\nlịch, làm việc với người Trung Quốc rồi. Để đạt được HSK cấp độ 4, bạn cần biết ít nhất 1200 từ tiếng Trung,\r\ntrong đó có khoảng 600 từ là những từ không thông dụng.', NULL, NULL);
INSERT INTO `tips` VALUES (37096, 'Hồ sơ học bổng Khổng Tử.\r\n- Scan trang ảnh hộ chiếu.\r\n- Chứng chỉ HSK, HSKK (có giá trị trong hai năm)\r\n- Bảng điểm hoặc học bạ: Các bạn mang đi dịch thuật công chứng sau đó scan lại bản dịch thuật công chứng đó.\r\n(Dùng để xin thư giới thiệu)\r\n- Một thư giới thiệu từ người phụ trách của cơ quan giới thiệu.\r\n- Tiến sĩ Giáo dục Quốc tế Trung Quốc phải cung cấp hai thư giới thiệu từ các giáo sư (bao gồm) hoặc các\r\nchuyên gia với các chức danh chuyên nghiệp đáng kể. Cung cấp nghiên cứu cá nhân của Trung Quốc (bao gồm kiến\r\nthức về chủ đề của chủ đề và phác thảo một kế hoạch nghiên cứu, khoảng 3.000 từ).\r\n- Thạc sĩ Giáo dục Quốc tế Trung Quốc phải cung cấp thư giới thiệu từ hai giáo sư trở lên. Ưu tiên được cung\r\ncấp cho những người có ý định làm việc như một cơ quan giảng dạy sau khi tốt nghiệp.\r\n- Giáo viên Trung Quốc tại chức phải đính kèm giấy chứng nhận việc làm và thư giới thiệu do cơ quan khánh\r\nthành.\r\n- Ứng viên dưới 18 tuổi phải nộp giấy chứng nhận có chữ ký của người giám hộ tại Trung Quốc.\r\n- Giấy khen, chứng nhận nếu có', NULL, NULL);
INSERT INTO `tips` VALUES (37097, '无功不受禄(wú gōng bù shòu lù) Không làm gì thì không nhận báo đáp.', NULL, NULL);
INSERT INTO `tips` VALUES (37098, 'Trong tiếng Hán cổ đại, 您 là hình thức hợp âm của 你们, vì vậy trong khẩu ngữ\r\nkhông có cách nói 您们, còn trong văn viết có thể chấp nhận được.', NULL, NULL);
INSERT INTO `tips` VALUES (37099, 'Bắt đầu xem các chương trình bằng tiếng Trung và phim có phụ đề bằng cả tiếng\r\nTrung và tiếng Việt có thể giúp bạn nhanh chóng tìm ra nghĩa của một từ vựng mới mà không cần phải tra từ\r\nđiển. Việc hiển thị phụ đề tiếng Trung sẽ hỗ trợ bạn cải thiện khả năng nói và viết.', NULL, NULL);
INSERT INTO `tips` VALUES (37100, 'Các nguyên âm “ü”. “üe”, khi ghép với phụ âm “l”, “n”, lúc viết hai dấu chấm\r\ntrên nguyên âm “ü” phải giữ nguyên. Ví dụ: \'nü\', \'nüe\', \'lü\', \'lüe\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37101, 'Nếu nhắc đến nền ẩm thực Trung Quốc, không thể bỏ qua món mì Trường Thọ - một\r\nmón ăn đã tồn tại trong suốt 300 năm ở tỉnh Chiết Giang. Chỉ cần nhắc đến tên gọi của nó, mọi người đều liên\r\ntưởng ngay đến sự tượng trưng cho lời chúc sống thọ và mong muốn một sức khoẻ vững chắc và lâu bền.', NULL, NULL);
INSERT INTO `tips` VALUES (37102, 'Học từ vựng thông qua nghĩa Hán Việt của từ.', NULL, NULL);
INSERT INTO `tips` VALUES (37103, 'Hai âm trong nhóm âm đầu lưỡi dễ gây nhầm lẫn là chữ ‘z’ và ‘c’\r\nz : để đầu lưỡi vào mặt trong răng trên , giữ thẳng lưỡi , không khí phát ra từ ở giữa , không bật hơi.\r\nc : là âm bật hơi của âm z , đầu lưỡi chạm vào mặt trong trăng trên , giữ lưỡi thẳng , không khí phát ra từ\r\nở giữa, bật hơi mạnh hơn ‘z’.', NULL, NULL);
INSERT INTO `tips` VALUES (37104, 'Lặp lại các từ vựng mới học một cách thường xuyên trong thời gian ngắn. Lấy ví\r\ndụ, ngày hôm nay bạn học 10 từ mới, ngày hôm sau bạn ôn lại 10 từ này và tiếp tục học 10 từ mới khác.', NULL, NULL);
INSERT INTO `tips` VALUES (37105, '请不要假装努力，结果不会陪你演戏。\r\nQǐng bú yào jiǎzhuāng nǔlì, jiéguǒ bú huì péi nǐ yǎnxì.\r\nXin đừng giả vờ cố gắng, kết quả sẽ không diễn kịch cùng bạn đâu.', NULL, NULL);
INSERT INTO `tips` VALUES (37106, '有爱的地方，就会有光。\r\nYǒu ài de dìfāng, jiù huì yǒu guāng.\r\nNơi nào có tình yêu, nơi đó có ánh sáng.', NULL, NULL);
INSERT INTO `tips` VALUES (37107, '好 (hǎo) còn có nghĩa là \'yes, ok, được\' để trả lời đồng ý với câu hỏi của đối\r\nphương.', NULL, NULL);
INSERT INTO `tips` VALUES (37108, '2. Thời điểm hai: 8 giờ đến 10 giờ sáng:\r\n- Vào thời điểm này, chúng ta thường cảm thấy tràn đầy năng lượng, não bộ dễ hưng phấn, trạng thái khả năng\r\ntư duy tốt nhất.\r\n- Thời điểm này là thời điểm tốt để vượt qua những vấn đề khó khăn và cần được tận dụng triệt để. Đây cũng\r\nlà khoảng thời gian tốt nhất để bạn sử dụng não bộ vào việc học tập một tri thức mới, đặc biệt là ngoại ngữ.\r\nBởi vậy mà thời gian học tập tại trường học thường nằm trong khoảng thời gian này.', NULL, NULL);
INSERT INTO `tips` VALUES (37109, 'Trung Hoa là đất nước phát hiện ra tơ lụa sớm nhất trên thế giới. Mà người phát\r\nminh ra tơ lụa là vợ của Thủy tổ Hiên Viên Hoàng Đế – Luy Tổ. Bà cũng được xưng là “Nhân văn nữ tổ”.', NULL, NULL);
INSERT INTO `tips` VALUES (37110, 'Phủ định nhiều lần HSK6\r\nLỗi sai phủ định nhiều lần thường do các từ biểu thị ý nghĩa phủ định như\r\n“避免”、“防止”、“以防”、“以免”、“切忌”、“禁止”、“阻止”、“劝止” gây ra. Các từ ngữ được nêu trên khi kết hợp với các từ phủ định như\r\n“不”、“没有” sẽ gây ra lỗi sai phủ định hai lần, khiến cho ý nghĩa của câu không phù hợp hoặc trái ngược lại với\r\nnội dung mà người nói muốn thể hiện.', NULL, NULL);
INSERT INTO `tips` VALUES (37111, 'Cách nhận biết lỗi sai:\r\n- Trong câu phủ định thường xuất hiện các thành phần biểu thị ý nghĩa phủ định, chúng ta có thể gọi đó là\r\nyếu tố phủ định. Khi nhìn thấy các câu phủ định, chúng ta cần phải lưu ý xem trong câu có mấy yếu tố phủ\r\nđịnh. Nếu có hai yếu tố phủ định, vậy câu thuộc dạng phủ định hai lần. Nếu có ba yếu tố phủ định, vậy câu sẽ\r\nthuộc dạng phủ định ba lần. Hãy luôn nhớ rằng, phủ định hai lần sẽ thể hiện ý nghĩa khẳng định, phủ định ba\r\nlần sẽ thể hiện ý nghĩa phủ định, như vậy thì bạn có thể kiểm tra được sự logic của câu.', NULL, NULL);
INSERT INTO `tips` VALUES (37112, 'Kết cấu của chữ Hán còn bao gồm kết cấu bao quanh một nửa và kết\r\ncấu bao quanh hoàn toàn.\r\nVí dụ: 店， 司... ...', NULL, NULL);
INSERT INTO `tips` VALUES (37113, 'Thực tế, ngay cả khi bạn cực kỳ chăm chỉ và học tiếng Trung trong nhiều năm, bạn\r\nsẽ không bao giờ chắc chắn 100% rằng bạn có thể hiểu hết tất cả mọi thứ mà người nói muốn truyền tải. Mười\r\nnăm trong hành trình học ngôn ngữ của bạn, đôi khi bạn phải yêu cầu ai đó nói chậm lại hoặc lặp lại những gì\r\nhọ nói vì bạn không hiểu. Điều này là chuyện rất bình thường!. Bạn có thể học tiếng Trung cực kỳ tốt thông\r\nqua việc cải thiện kỹ năng nghe của mình, nhưng hãy cho bản thân nghỉ ngơi khi nói đến sự hoàn hảo.', NULL, NULL);
INSERT INTO `tips` VALUES (37114, 'Hành thư là dạng chữ nằm giữa Khải thư và Thảo thư, được sử dụng\r\nphổ biến trong thời Tam Quốc và thời nhà Tần.\r\n\'Bát đao phân mễ phấn\'\r\nPhân hạt gạo bằng 8 dao, hạt gạo sẽ nhuyễn ra như bột ( phấn ). Chữ BÁT 八 chồng lên chữ ĐAO 刀 thành chữ PHÂN\r\n分, chữ PHÂN 分 ghép với chữ MỄ 米 thành chữ PHẤN 粉.\'\r\n\'冤种(yuān zhǒng)\' chỉ người có \'duyên nợ\' với bạn, bám \'ÁM\' bạn mãi không thôi.', NULL, NULL);
INSERT INTO `tips` VALUES (37115, 'Phấn trang điểm là mỹ phẩm được phụ nữ Trung Hoa thời xưa dùng để thoa lên da,\r\ncó tác dụng làm da sáng bóng. Có ba loại phấn thường được sử dụng đó là: phấn bột gạo (米粉), bột chì (胡粉) và\r\nphấn thơm (香粉).', NULL, NULL);
INSERT INTO `tips` VALUES (37116, 'Quy tắc trái trước phải sau：\r\nCác bộ nằm bên trái viết trước rồi đến các bộ ở bên phải. Ví dụ chữ minh “明” gồm bộ nhật và bộ nguyệt. Bộ\r\nnhật bên trái nên viết trước sau đó viết đến bộ nguyệt, vẫn theo quy tắc các nét trên trước dưới sau , trái\r\ntrước phải sau.\r\n\'三人行，必有我师\' mang nghĩa khi đi cùng hai người nữa thì trong đó chắc chắn\r\ncó một người có thể là thầy ta. Hàm ý của câu này là người khác có thể có những ưu điểm\r\nmà chúng ta không có, những khuyết điểm mà chúng ta nên nhìn nhận.', NULL, NULL);
INSERT INTO `tips` VALUES (37117, 'Thừa thành câu trong HSK6\r\nChỉ trong một câu đồng thời xuất hiện cặp từ đồng nghĩa hoặc gần nghĩa, khiến ý nghĩa của câu trùng lặp,\r\nrườm rà. Loại lỗi sai này kiểm tra mức độ hiểu và sử dụng từ vựng của thí sinh.\r\nCách phát hiện lỗi sai:\r\n- Hai thành phần trùng lặp sẽ thường xuất hiện ngay cạnh nhau. Vì thế khi đọc đề, các bạn cần chú ý ý nghĩa\r\ncủa những từ gần nhau có hoàn toàn tương đồng hay không. Nếu xuất hiện hiện tượng đồng nghĩa thì rất có thể\r\ncâu đó có lỗi thừa thành phần câu.', NULL, NULL);
INSERT INTO `tips` VALUES (37118, 'Khi âm tiết bắt đầu bằng a,o hay e theo sau âm tiết khác, ta phải dùng\r\ndấu cách âm (\') để tách các âm tiết nhằm tránh lẫn lộn về ranh giới giữa các âm tiết.\r\nVí dụ: xiān và Xī\'ān', NULL, NULL);
INSERT INTO `tips` VALUES (37119, '我有一点点想点一点一点点喝。\r\nBạn đoán được ý nghĩa câu này không?', NULL, NULL);
INSERT INTO `tips` VALUES (37120, 'Ẩm thực Trung Hoa gồm có 8 trường phái: Sơn Đông, Quảng Đông, Tứ Xuyên, Hồ Nam,\r\nPhúc Kiến, Chiết Giang, Giang Tô và An Huy.', NULL, NULL);
INSERT INTO `tips` VALUES (37121, 'Ai cũng có thể \'bắt đầu\' nhưng không phải ai cũng đi được tới đích.', NULL, NULL);
INSERT INTO `tips` VALUES (37122, 'Các cuộc thi thường niên có chứng chỉ siêu xịn dành cho dân học tiếng\r\nTrung &lt;3\r\n1. Tôi là diễn thuyết gia.\r\n2. Tranh biện tiếng Hoa\r\n3. Dịch nói giỏi\r\n4. Nhịp cầu Hán ngữ', NULL, NULL);
INSERT INTO `tips` VALUES (37123, '吧 (ba) còn có cách nói khác như 呗 (bei)、啵 (bo).', NULL, NULL);
INSERT INTO `tips` VALUES (37124, '题目: 天涯海角\r\n小朋友写: 妹妹乱跑跑到天涯海角。\r\n老师评语: 你妹妹真会跑~~', NULL, NULL);
INSERT INTO `tips` VALUES (37125, 'Chữ \'结\' có hai âm đọc là \'jié\' và \'jiē\', chỉ đọc là \'jiē\' trong một số\r\ntrường hợp như:\r\n- 结巴\r\n- 结实\r\n- 开花结果', NULL, NULL);
INSERT INTO `tips` VALUES (37126, 'Giấy tờ cần chuẩn bị để xin du học Trung Quốc 1 năm\r\n- Bảng điểm dịch thuật công chứng\r\n- Bằng tốt nghiệp công chứng\r\n- Form sơ yếu lí lịch của trường (trực tiếp liên hệ trường bạn muốn xin vào để nhận)\r\n- Hộ chiếu photo công chứng\r\n- Giấy khám sức khỏe du học\r\n- Giấy đăng ký nhập học (trực tiếp liên hệ trường bạn muốn xin vào để nhận)\r\n- Cùng nhiều giấy tờ đặc thù khác (Giấy JW, giấy chứng nhận tốt nghiệp…)', NULL, NULL);
INSERT INTO `tips` VALUES (37127, 'Khải thư là dạng chữ bắt đầu xuất hiện vào cuối thời nhà Hán và\r\nlưu hành mãi cho đến ngày nay.', NULL, NULL);
INSERT INTO `tips` VALUES (37128, 'Đọc câu đố đoán từ vựng:\r\n人脱衣服，它穿衣服，人脱帽子，它戴帽子。\r\nRén tuō yīfú, tā chuān yīfu, rén tuō màozi, tā dài màozi.', NULL, NULL);
INSERT INTO `tips` VALUES (37129, 'Hướng dẫn sử dụng: tính năng CHECK lỗi câu:\r\n1. Nhập câu cần check tại ô \'Tra cứu\'.\r\n2. Lỗi hiện bên dưới.', NULL, NULL);
INSERT INTO `tips` VALUES (37130, 'Chữ “Như” (如) và chữ “Tâm” (心) hợp lại thành chữ “Thứ” (恕), nghĩa là tha thứ,\r\nbao dung. Chữ “Như” ghép với chữ “Tâm” mang ý nghĩa là để cho trái tim gần với trái tim, để tâm mình gần với\r\ntâm người, tâm người cũng như tâm mình.', NULL, NULL);
INSERT INTO `tips` VALUES (37131, 'Chữ \'华\' có hai âm đọc là \'huá\' và \'huà\', chỉ đọc là \'huà\' trong một số\r\ndanh từ riêng:\r\n- 华佗\r\n- 华山\r\n- 华阴\r\n- 华洲', NULL, NULL);
INSERT INTO `tips` VALUES (37132, 'Bạn nghĩ nó là số 1 à???', NULL, NULL);
INSERT INTO `tips` VALUES (37133, 'Người Trung Quốc kiêng tặng giày vì chữ Hán của ‘giày’ (鞋 xié) có một từ đồng âm\r\nlà ‘ác’ (邪 xié). Hãy cẩn thận, bạn bè của bạn có thể mất liên lạc do nhận được một đôi giày từ bạn.', NULL, NULL);
INSERT INTO `tips` VALUES (37134, 'Đọc câu đố đoán từ vựng:\r\n一个黑孩，从不开口，要是开口，掉出舌头。\r\nYīgè hēi hái, cóng bù kāikǒu, yàoshi kāikǒu, diào chū shétou.', NULL, NULL);
INSERT INTO `tips` VALUES (37135, 'Cần chú ý phát âm thật chuẩn các âm /sh/, /z/, /ch/, /zh/, /n/, /l/, /q/, /j/….\r\nvà đúng thanh điệu, đặc biệt là thanh 1 và thanh 4.', NULL, NULL);
INSERT INTO `tips` VALUES (37136, 'Trượt HSKK đỗ HSK vẫn được cấp chứng chỉ.', NULL, NULL);
INSERT INTO `tips` VALUES (37137, 'Học bổng Chính phủ (CSC)\r\nHọc bổng Chính phủ là học bổng được Bộ Giáo dục Trung Quốc cung cấp, hỗ trợ các bạn học sinh/sinh viên có\r\nthành tích cao, có cơ hội đến Trung Quốc du học.\r\nTiêu chuẩn:\r\n- Miễn học phí, ký túc xá, phí bảo hiểm y tế\r\n- Cung cấp sinh hoạt phí (2500-3500 tệ/tháng tùy bậc học)\r\nYêu cầu:\r\n- Bậc đại học: Yêu cầu tối thiểu HSK 3, GPA 7.5 trở lên\r\n- Bậc thạc sĩ/tiến sĩ: Yêu cầu tối thiểu HSK 4, GPA 7.5 trở lên', NULL, NULL);
INSERT INTO `tips` VALUES (37138, '\'个\' là lượng từ \'quốc dân\' bởi trong tiếng Trung nó thường được dùng\r\ntrước những danh từ không có lượng từ riêng.', NULL, NULL);
INSERT INTO `tips` VALUES (37139, 'Học bổng “Một vành đai một con đường” là học bổng được Bộ Giáo dục Trung Quốc\r\ncung cấp, hỗ trợ các bạn học sinh sinh viên, ở những đất nước ven “Con đường tơ lụa”, có thành tích tốt, có\r\ncơ hội đến Trung Quốc học tập. Mỗi năm, Trung Quốc cung cấp 10.000 học bổng “Một vành đai một con đường” cho\r\ncác nước ven “Con đường tơ lụa”.\r\nTiêu chuẩn:\r\n- Miễn học phí, ký túc xá, phí bảo hiểm y tế\r\n- Cung cấp sinh hoạt phí (1000-3000 tệ/tháng tùy bậc học)\r\nYêu cầu:\r\n- Bậc đại học: Yêu cầu tối thiểu có HSK 4\r\n- Bậc thạc sĩ: Yêu cầu tối thiểu có HSK 5', NULL, NULL);
INSERT INTO `tips` VALUES (37140, 'Trong văn viết, chúng ta có thể thay thế 爸爸 (bàba) bằng 父亲 (fùqīn)，妈妈 (māma)\r\nbằng 母亲 (mǔqīn).', NULL, NULL);
INSERT INTO `tips` VALUES (37141, 'Phần thi này chủ yếu kiểm tra cách phát âm của bạn, vậy nên bạn hãy luyện phát\r\nâm thật chuẩn, khi nói không cần nói quá nhanh mà điều chỉnh tốc độ nói vừa phải sao cho phát âm thật tròn\r\nvành, rõ chữ.', NULL, NULL);
INSERT INTO `tips` VALUES (37142, '生活总是给你第二次机会。 这叫做明天。\r\nShēnghuó zǒng shì gěi nǐ dì’èr cì jīhuì. Zhè jiàozuò míngtiān.\r\nCuộc sống luôn cho bạn một cơ hội thứ hai. Nó được gọi là ngày mai.', NULL, NULL);
INSERT INTO `tips` VALUES (37143, 'Phần thi miêu tả tranh chúng ta cần quan sát tỉ mỉ và trả lời 5 câu\r\nhỏi dưới đây:\r\n- 他/她是谁？\r\n- 他/她在那里？\r\n- 他/她在干什么？\r\n- 他/她为什么这么做？\r\n- 后来怎么样了？', NULL, NULL);
INSERT INTO `tips` VALUES (37144, 'Tự giác rèn luyện.', NULL, NULL);
INSERT INTO `tips` VALUES (37145, 'Học bằng trí tưởng tượng.', NULL, NULL);
INSERT INTO `tips` VALUES (37146, 'Vận mẫu “e” có hai cách đọc, đọc là “ơ” nếu không có thanh điệu và đọc thanh\r\n“ưa” nếu có thanh điệu đi kèm.', NULL, NULL);
INSERT INTO `tips` VALUES (37147, 'Tứ đại văn hóa phi vật thể Trung Quốc gồm: Võ thuật Trung Hoa, Y học, Kinh kịch,\r\nThư pháp', NULL, NULL);
INSERT INTO `tips` VALUES (37148, '4. Thời điểm bốn: Trước khi đi ngủ một tiếng:\r\nHãy dùng khoảng thời gian này để khắc sâu kiến thức sau một ngày học tập, đặc biệt là ôn lại một số ngữ pháp\r\nkhó nhằn hoặc từ vựng khó nhớ.', NULL, NULL);
INSERT INTO `tips` VALUES (37149, '“没有过不去的火焰山(Méiyǒu guòbuqù de huǒyàn shān) Không có khó khăn nào là không vượt\r\nqua được.', NULL, NULL);
INSERT INTO `tips` VALUES (37150, 'Bộ thủ là gì?\r\n- Bộ thủ là khái niệm, là thuật ngữ do các nhà biên soạn từ điển đặt ra để phân chia mục từ, giúp người đọc\r\ncó thể tra từ nhanh nhất có thể. Bộ thủ được lấy từ hình thể của thiên bàng (người biên soạn từ điển có thể\r\nsẽ lấy thiên bàng biểu ý hoặc thiên bàng biểu âm, đa phần sẽ lấy thiên bàng biểu ý, tức hình bàng).\r\nBộ thủ xuất hiện ở cả chữ đơn thể (独体字), chữ hợp thể (合体字) và cả nét bút không thể tạo nên chữ. Ví dụ:\r\n- Chữ đơn thể (独体字): bộ Đao (刀), bộ Ngôn (言), bộ Nhân (人), bộ Tâm (心), bộ Thủy (水),…\r\n- Chữ hợp thể (合体字): bộ Đao (刂) trong chữ 别, bộ Ngôn (讠) trong chữ 请, bộ Nhân (亻) trong chữ 他, bộ Tâm (忄)\r\ntrong chữ 情, bộ Thủy (氵) trong chữ 河,…\r\n- Nét bút, hay còn gọi là nét chữ (笔画): bộ Chủ (丶), bộ Cổn (丨), bộ Phiệt (丿),…', NULL, NULL);
INSERT INTO `tips` VALUES (37151, 'Tìm một người bạn đồng hành.', NULL, NULL);
INSERT INTO `tips` VALUES (37152, 'Vào dịp tết đến xuân về, người Hoa thường dán ngược chữ \'Phúc\' để đón phước lành\r\ncủa năm mới. Bởi từ \'到 (dào)\' mang nghĩa tới, đến đồng âm với từ \'倒 (dào)\' mang nghĩa đảo ngược.\r\nNgoài dán chữ \'Phúc\' như vậy ra, người ta còn dán ngược chữ “春（xuân）” với ngụ ý xuân đến.', NULL, NULL);
INSERT INTO `tips` VALUES (37153, 'Cách phân chia thời gian đọc bài viết HSK6\r\nCó nhiều cách để chia 10 phút như 2/3/5, 3/3/4, 2/3/4/1 3/4/3 và n cách khác. Trong quá trình ôn thi, các\r\nbạn có thể lựa chọn cách chia phù hợp nhất với mình. Cá nhân mình thì mình chọn cách chia 3/4/3 để đọc bài\r\nvà phần này mình sẽ giới thiệu mình đã làm những gì trong 10 phút này nhé.\r\n- 3 phút đầu tiên: Đọc qua toàn bài để xác định nội dung chính, cùng lúc đó mình sẽ khoanh tròn những tên\r\nriêng có trong bài.\r\n- 4 phút tiếp theo: Đọc và gạch chân các mốc thời gian, thời điểm hoặc các địa điểm có trong bài. Cùng với\r\nđó là xác định thứ tự các ý chính có trong bài.\r\n- 3 phút tiếp theo: Đọc kỹ hơn để xem lại và thuộc KEYWORDS và những câu “đắt” trong bài, lượt lại một lần\r\nnhững gì đã đánh dấu, hình thành dàn ý sơ bộ trong đầu, lúc này hãy nhìn thật kỹ những tên riêng và các từ\r\nmà bạn định sử dụng trong phần viết.\r\nVà NGAY SAU KHI thu lại văn bản gốc, bạn hãy NGAY LẬP TỨC ghi nhanh những TÊN RIÊNG quan trọng của bài vào\r\nnháp để tránh quên chữ Hán.', NULL, NULL);
INSERT INTO `tips` VALUES (37154, '打烊, đọc là dǎ yàng, nghĩa là “đóng cửa” (关门), đây là từ được đại đa số người\r\nTrung Quốc dùng khi muốn thông báo cửa hàng hoặc dịch vụ của họ tạm thời đóng cửa sau một ngày làm việc, sau\r\nmột buổi làm việc hoặc trong một kỳ nghỉ lễ tết.', NULL, NULL);
INSERT INTO `tips` VALUES (37155, 'Thường ngày cần học và ôn luyện các cách biểu đạt quan điểm như:\r\n- 我认为\r\n- 简单地说\r\n- 我以为\r\n- 对我来说\r\n- 实际上是', NULL, NULL);
INSERT INTO `tips` VALUES (37156, 'Trường phái ẩm thực Sơn Đông được coi như đệ nhất trường phái ẩm thực Trung Hoa\r\nvới nền ẩm thực vô cùng tinh túy và phát triển lâu đời. Ẩm thực Sơn Đông thường mang hương vị ấm, nồng đặc\r\ntrưng bởi các món chiên, rán, nướng, hấp và sử dụng nhiều hành, tỏi trong chế biến.', NULL, NULL);
INSERT INTO `tips` VALUES (37157, '人生就像一幅画，应该多一些亮丽的色彩，少一些灰暗的色调。\r\nRénshēng jiù xiàng yì fú huà, yīnggāi duō yìxiē liànglì de sècǎi, shǎo yìxiē huī\'àn de sèdiào.\r\nCuộc sống giống như một bức tranh, nên có nhiều gam màu tươi sáng và bớt đi những gam màu tối.', NULL, NULL);
INSERT INTO `tips` VALUES (37158, 'Kết cấu 8 nét cơ bản\r\nNét chấm(点): một dấu chấm viết từ trên xuống. Có nét chấm ngắn, chấm dài tùy theo chữ.\r\nNét ngang(横): là một nét ngang viết từ trái sang phải.\r\nNét sổ thẳng(竖): là một nét thẳng đứng viết từ trên xuống dưới.\r\nNét móc(钩): là nét móc lên ở điểm kết thúc của các nét khác.\r\nNét hất(提): là nét viết hất từ trái sang phải.\r\nNét cong(弯): có một nét viết cong đi từ trên xuống.\r\nNét phẩy(撇): là nét bút đi xuống từ phải qua trái.\r\nNét mác(捺): nét viết thẳng từ trái qua phải, từ trên xuống.', NULL, NULL);
INSERT INTO `tips` VALUES (37159, '1. Thời điểm một: Sau khi thức dậy vào sáng sớm:\r\n- Sau khi thức dậy vào sáng sớm, bộ não bạn sau một đêm nghỉ ngơi đã loại bỏ được những mệt mỏi của ngày hôm\r\ntrước, bắt đầu một trạng thái hoạt động mới, vô cùng tỉnh táo.\r\n- Lúc này, ấn tượng sẽ rất rõ ràng, và việc ghi nhớ kiến thức vào não bộ cũng sẽ dễ dàng hơn.', NULL, NULL);
INSERT INTO `tips` VALUES (37160, '一 (yī)，不 (bù) khi đứng trước thanh 4 thì nó sẽ biến thành thanh 2.\r\nVí dụ: 一样、一定、不是、不看', NULL, NULL);
INSERT INTO `tips` VALUES (37161, 'Quy tắc vào trước đóng sau:\r\nĐối với những chữ có dạng hộp , khi viết hết nét bên ngoài , chỉ chừa lại nét ở bên dưới. Sau đó viết các\r\nthành phần bên trong rồi đóng hộp lại. Nét bên dưới giống như là cái nắp, sau khi bỏ hết đồ vào rồi thì đóng\r\nlại. Lấy ví dụ ở đây là chữ “酒”.\r\n\'半斤对八两(bàn jīn duì bā liǎng)\' kẻ tám lạng, người nửa cân', NULL, NULL);
INSERT INTO `tips` VALUES (37162, 'Giống như Tiếng Việt, trong Tiếng Trung, chúng ta có thể dùng 2 số liên tiếp để\r\ndiễn đạt số ước lượng.', NULL, NULL);
INSERT INTO `tips` VALUES (37163, 'Đánh giá, cho điểm thí sinh dựa theo tiêu chí dưới đây:\r\n- Phần thi chủ yếu kiểm tra khả năng diễn đạt đoạn văn, vì vậy phần trả lời cần phải có ít\r\nnhất năm câu và xoay quanh chủ đề câu hỏi.\r\n- Thi sinh sẽ đạt điểm số cao khi có thể trả lời câu hỏi với nội dung phong phú, diễn đạt lưu\r\nloát, ít ngắt quãng, lặp lại, sai ngữ pháp.\r\n- Thí sinh sẽ đạt điểm số trung bình khi có thể trả lời câu hỏi nhưng lượng thông tin khá ít,\r\nngắt quãng, lặp lại, sai ngữ pháp khá nhiều.\r\n- Thí sinh sẽ đạt điểm số thấp khi nội dung trả lời không liên quan tới câu hỏi, lượng thông tin\r\nrất ít.\r\n- Thí sinh không trả lời, xem như không điểm.', NULL, NULL);
INSERT INTO `tips` VALUES (37164, 'Đừng hạ tone và nói quá dứt khoát khi nói tiếng Trung.', NULL, NULL);
INSERT INTO `tips` VALUES (37165, 'Bí kíp chuẩn bị thi HSKK trung cấp:\r\n- Tập hét, gào thật to\r\n- Luyện nghe những đoạn ngắn.\r\n- Tập ghi keyword (trong lúc thi cố gắng xác định trọng tâm.\r\n- Tập kỹ năng \'bịa\'', NULL, NULL);
INSERT INTO `tips` VALUES (37166, 'Học bổng Giáo viên tiếng Trung Quốc Quốc tế (CIS)\r\nHọc bổng Khổng Tử, là học bổng do học viện Khổng Tử Trung Quốc lập ra, dành cho những người có thành tích\r\ntiếng Trung tốt, đến Trung Quốc học tập. Học bổng CIS có cơ cấu cao, tương đương với CSC toàn phần.\r\nTiêu chuẩn:\r\n- Miễn học phí, ký túc xá, phí bảo hiểm y tế\r\n-nCung cấp sinh hoạt phí (2500-3000 tệ/tháng tùy bậc học)\r\nYêu cầu:\r\n- Bậc đại học: Yêu cầu tối thiểu có HSK 4 (210 điểm trở lên), HSKK trung cấp (60 điểm trở lên), GPA 7.0 trở\r\nlên\r\n- Bậc thạc sĩ: Yêu cầu tối thiểu có HSK 5 (210 điểm), HSKK trung cấp (60 điểm trở lên), GPA 7.0 trở lên\r\n\'扎心\' ngôn ngữ mạng \'đắng lòng\'', NULL, NULL);
INSERT INTO `tips` VALUES (37167, 'Thử chút vè đọc nhịu không:\r\n任命是任命\r\n人名是人名\r\n任命不能说成人名\r\n人名也不能说成任命。\r\nPinyin:\r\nRènmìng shì rènmìng\r\nrénmíng shì rénmíng\r\nrènmìng bùnéng shuō chéngrénmíng\r\nrén míng yě bùnéng shuō chéng rènmìng.', NULL, NULL);
INSERT INTO `tips` VALUES (37168, '二 (èr) và 两 (liǎng) đều có nghĩa là 2, nhưng cách sử dụng thì khác nhau.', NULL, NULL);
INSERT INTO `tips` VALUES (37169, 'Người Trung Quốc cực kỳ kiêng kị số 4. Đó cũng là lý do các tòa nhà tại đây\r\nthường không có tầng 4, 14 hay 24… do cách phát âm đồng nghĩa với \'cái chết\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37170, 'Các món ăn Giang Tô cũng được coi như những tác phẩm nghệ thuật với cách trình\r\nbày vô cùng cầu kỳ và bắt mắt. Các món ăn Giang Tô chú trọng đến việc dùng dao trong chế biến, món ăn tinh\r\ntế, khẩu vị thanh đạm. Đặc biệt các món ăn Giang Tô không dùng xì dầu để giữ được màu sắc của món ăn và\r\nthường sử dụng nhiều đường với giấm khi chế biến tạo nên hương vị chua, ngọt đặc trưng. Các món ăn bạn phải\r\nthưởng thức khi đến Giang Tô là: thịt và thịt cua hấp.\r\n\'养鱼\' từ vựng ngôn ngữ mạng mang ý nghĩa \'Long đen\', chắc hẳn các rượu thủ không\r\nthể không biết từ này.\r\nVí dụ: 让我看看谁养鱼: Để tôi xem còn ai để lại long đen.\r\n\'一步走错步步错\' mang nghĩa \'đi sai một bước, các bước kế tiếp đều sai\'', NULL, NULL);
INSERT INTO `tips` VALUES (37171, 'Nét bút (笔画) là đơn vị nhỏ nhất cấu tạo nên một chữ Hán.', NULL, NULL);
INSERT INTO `tips` VALUES (37172, 'Dấu chấm chéo ( 、) dùng để liệt kê, ngăn cách các từ hoặc ngữ có quan hệ ngang\r\nbằng trong câu.', NULL, NULL);
INSERT INTO `tips` VALUES (37173, 'Cuộc thi Tranh Biện Tiếng Hoa được tổ chức hàng năm bởi CLB Tiếng Trung trường\r\nĐại học Ngoại Thương nhằm cung cấp sân chơi bổ ích cho các bạn học sinh và sinh viên yêu thích tiếng Trung\r\ntrên toàn miền Bắc. Đây là cơ hội để các thí sinh rèn luyện kỹ năng tranh luận, hùng biện, và nâng cao năng\r\nlực ngôn ngữ, từ đó có thể bày tỏ quan điểm của mình về những vấn đề xã hội.', NULL, NULL);
INSERT INTO `tips` VALUES (37174, 'Khi tham gia cuộc thi, các thí sinh sẽ có cơ hội nhận được nhiều phần thưởng có giá trị, bao gồm giấy chứng\r\nnhận tham gia cuộc thi, giải Quán quân trị giá 10.000.000 VND, giải Á quân 1 trị giá 7.000.000 VND, giải Á\r\nquân 2 trị giá 5.000.000 VND và hàng ngàn voucher và ưu đãi khác. Tổng giá trị giải thưởng lên đến hơn 170\r\ntriệu đồng.', NULL, NULL);
INSERT INTO `tips` VALUES (37175, 'Họ 周 trong Tiếng Trung khi dịch ra Tiếng Việt có thể dịch thành hai họ đó là\r\nChâu hoặc Chu.', NULL, NULL);
INSERT INTO `tips` VALUES (37176, 'Tứ bảo văn phòng là chỉ bốn loại dụng cụ dùng trong văn phòng của người Trung\r\nHoa xưa bao gồm: Bút, mực, giấy, nghiên.', NULL, NULL);
INSERT INTO `tips` VALUES (37177, '“刺客 (Sát thủ)” được dùng để chỉ “một người sử dụng vũ khí để ám sát người khác”.\r\nNhưng “××刺客” trong trường hợp này được dùng để chỉ một mặt hàng trông rất bình thường nhưng lại có giá cao\r\nngút trời, mang tính “ẩn mình” như “刺客 (Sát thủ)”. Khi thanh toán, giá cả cao hơn dự đoán nên như đâm một\r\nnhát dao vào khách hành. Ví dụ có: 雪糕刺客, 水果刺客,…', NULL, NULL);
INSERT INTO `tips` VALUES (37178, '看门诊 (kān ménzhěn): động từ nghĩa là khám bệnh (chỉ khám thôi không nhập viện)\r\n住院 (zhùyuàn) = 入院 (rùyuàn): nhập viện, nằm viện.', NULL, NULL);
INSERT INTO `tips` VALUES (37179, '\'躺着也中枪\' ngôn ngữ mạng \'nằm không cũng dính đạn\'', NULL, NULL);
INSERT INTO `tips` VALUES (37180, '如果机会没有敲门，那就建一扇门。\r\nRúguǒ jīhuì méiyou qiāo mén, nà jiù jiàn yí shàn mén.\r\nNếu cơ hội không gõ cửa, hãy tạo ra một cánh cửa.', NULL, NULL);
INSERT INTO `tips` VALUES (37181, 'Trong tiếng Trung, một số tính từ có thể trùng điệp để biểu thị \'mức độ mạnh\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37182, '演讲 (jiǎngyán) và 讲演 (yǎnjiǎng) đều có nghĩa là “diễn thuyết”. 觉察 (juéchá) và 察觉\r\n(chájué) đều có nghĩa là phát hiện. Mặc dù trật tự từ khác nhau nhưng chúng vẫn có nghĩa giống nhau.\r\n\'回南天(huí nántiān)\' Trời nồm, ẩm', NULL, NULL);
INSERT INTO `tips` VALUES (37183, '3. Thời điểm ba: 18 giờ đến 20 giờ tối:\r\n- Đây cũng là thời gian tốt để não bộ của bạn tiếp thu và ghi nhớ ngoại ngữ. Nhiều người sử dụng khoảng thời\r\ngian này để xem xét, khắc sâu ấn tượng, tổng kết và sắp xếp những kiến thức ngữ pháp, từ vựng của ngoại ngữ.\r\n- Trong khoảng thời gian này, não bộ sẽ bắt đầu hoạt động trở lại và phản ứng nhanh hơn.', NULL, NULL);
INSERT INTO `tips` VALUES (37184, '只要你愿意，当你失落失意的时候，需要一个肩膊的时候，告诉我，我会立即出现。\r\nZhǐyào nǐ yuànyì, dāng nǐ shīluò shīyì de shíhou, xūyào yí gè jiānbó de shíhou, gàosù wǒ, wǒ huì lìjí\r\nchūxiàn.\r\nChỉ cần em muốn, khi em thất vọng, khi cần một bờ vai, hãy nói với anh, anh sẽ lập tức xuất hiện.', NULL, NULL);
INSERT INTO `tips` VALUES (37185, 'Trong tiếng trung tiền mừng tuổi là \'压岁钱\'，nhưng đúng ra chữ \'岁\' này phải là chữ\r\n\'祟\' (trong từ 邪祟/tà ma,xui rủi,những điều không may mắn) này; 压/trấn áp,đẩy lùi,nén giữ.\r\nCho nên nghĩa chính xác của tiền mừng tuổi chính là tiền xua đuổi tà ma, xua đuổi những điều không may\r\nmắn,đen đủi cầu bình an cho gia chủ.Do đồng âm nên người ta lấy luôn từ \'岁\' tuổi tác này.', NULL, NULL);
INSERT INTO `tips` VALUES (37186, 'Vì các câu trong đề đa phần đều là câu ngắn khá đơn giản, nên bạn hãy cố gắng\r\ntập trung nghe và thuật lại một cách chính xác, không thiếu không thừa một chữ nào.\r\n\'药不治假病不解真愁 (yào bùzhì jiǎ bìng bù jiě zhēn chóu)\' Tâm bệnh không chữa được bằng\r\nthuốc.', NULL, NULL);
INSERT INTO `tips` VALUES (37187, 'Khi nói về số nhà, địa chỉ, số điện thoại thì \'số 1\' đọc phiên âm là \'yāo\',\r\nkhông thể đọc là \'yī\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37188, 'TOP 5 CHƯƠNG TRÌNH LUYỆN NGHE TRÊN XIMALAYA\r\n1. “MỘT NGƯỜI NGHE”（一个人听）\r\n2. “DẠ ĐỘC” 夜读 KHỔ QUA\r\n3. THÌ RA LÀ VẬY? 原来是这样?\r\n4. LUÔN CÓ MỘT BÀI CA CHỈ MUỐN NGHE MỘT MÌNH\r\n5. ĐOẠN TỬ ĐẾN RỒI! 段子来了', NULL, NULL);
INSERT INTO `tips` VALUES (37189, 'Trường phái ẩm thực Quảng Đông có thể coi là trường phái có nhiều cách chế biến\r\nmón ăn nhất, có đến 21 cách chế biến khác nhau. Trường phái ẩm thực Quảng Đông chú trọng đến bốn yếu tố\r\nchính là hương, sắc, vị và hình với đòi hỏi vô cùng khắt khe cho các món ăn: non mà không sống, tươi mà\r\nkhông thô, mỡ mà không ngấy, thanh mà không nhạt, mùa xuân hạ món ăn phải thanh mát, mùa thu đông món ăn\r\nphải ấm và đậm vị.', NULL, NULL);
INSERT INTO `tips` VALUES (37190, 'Trong tiếng Trung, giữa số từ và danh từ bắt buộc phải thêm lượng từ.', NULL, NULL);
INSERT INTO `tips` VALUES (37191, 'Giày. Từ tiếng Quan Thoại có nghĩa là giày (鞋 xié) là từ đồng âm của 邪 (xié),\r\nhay “ác quỷ”. Vì vậy việc tặng ai đó một đôi giày được cho là điềm gở.', NULL, NULL);
INSERT INTO `tips` VALUES (37192, 'Hãy “đắm mình trong môi trường ngôn ngữ”, nghe tiếng Trung khi bạn nấu cơm, đánh\r\nrăng, khi make up, trước khi đi ngủ,...', NULL, NULL);
INSERT INTO `tips` VALUES (37193, 'Gắn nhãn cho sự vật trong môi trường xung quanh.', NULL, NULL);
INSERT INTO `tips` VALUES (37194, 'Chữ \'哪\' có bốn âm đọc là \'nǎ\', \'né\' , \'něi\' và \'na\'.\r\n- Đọc là \'nǎ\' chủ yếu.\r\n- Đọc là \'něi\' trong khẩu ngữ.\r\n- Đọc là \'na\'.trong biến âm với 啊.\r\n- Đọc là \'né\' trong từ 哪吒 (Na Tra)', NULL, NULL);
INSERT INTO `tips` VALUES (37195, 'Nhớ âm Hán Việt là cách vô cùng hiệu quả để giúp bạn ghi nhớ nghĩa của từ vựng\r\nđồng thời đoán được nghĩa của những từ vựng mà có thể bạn chưa từng học trước đó.\r\n\'水\' có hình dạng giống khe suối, biểu thị dòng nước chảy.', NULL, NULL);
INSERT INTO `tips` VALUES (37196, 'Đối tượng cần xin thư chấp thuận: Sinh viên apply hệ thạc sĩ, tiến sĩ.', NULL, NULL);
INSERT INTO `tips` VALUES (37197, 'Khi nào có kết quả học bổng CSC?\r\nKết quả Học bổng CSC sẽ được các trường Đại học Trung Quốc công bố bắt đầu từ tháng 6.', NULL, NULL);
INSERT INTO `tips` VALUES (37198, 'Chữ “Quý” (愧) nghĩa là hổ thẹn, bao gồm bộ “Tâm” (忄) là tim, và chữ “Quỷ” (鬼)\r\nnghĩa là ma quỷ. Con người ai cũng có hai mặt thiện và ác, có Phật tính và ma tính, có thiện tâm và ma tâm.\r\nNếu thiện tâm làm chủ thì đó là người tốt, còn nếu ma tâm làm chủ thì chẳng phải đó sẽ là người xấu sao?', NULL, NULL);
INSERT INTO `tips` VALUES (37199, 'Chú ý bố cục (mở bài, thân bài, kết bài) ngữ pháp, văn phong diễn\r\nđạt.', NULL, NULL);
INSERT INTO `tips` VALUES (37200, 'Một món ăn khiến người ta cảm thấy e sợ chỉ bởi tên gọi, nhưng đừng vội hoảng\r\nsợ, bởi khi bạn thưởng thức nó, bạn sẽ được trải nghiệm một điều bất ngờ. Đó chính là món Đậu phụ Trường Thọ\r\n- sự kết hợp độc đáo giữa đậu phụ tán nhuyễn, cà rốt tươi mát và xúc xích thơm ngon, tạo nên một hòa quyện\r\nvị giòn rụm đầy hấp dẫn. Không chỉ thỏa mãn vị giác mà món này còn mang lại lợi ích dinh dưỡng vô cùng quan\r\ntrọng cho sức khỏe.', NULL, NULL);
INSERT INTO `tips` VALUES (37201, 'Chữ Hán có hai loại kết cấu, nếu được tạo thành bởi một bộ phận\r\nduy nhất thì đó là chữ Hán có kết cấu đơn lẻ. nếu được tạo thành bởi hai bộ phận hoặc\r\nnhiều hơn thì đó là chữ Hán có kết cấu tổ hợp.', NULL, NULL);
INSERT INTO `tips` VALUES (37202, '再见 là một từ để chào tạm biệt. Mang ý nghĩa là lần sau gặp lại bởi 再 có nghĩa là\r\n\'Một lần nữa\' và “见” mang nghĩa là \'gặp nhau\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37203, 'Câu trần thuật thường có ngữ điệu xuống giọng.', NULL, NULL);
INSERT INTO `tips` VALUES (37204, 'Nguyệt Nha Tuyền hồ nước hình trăng lưỡi liềm, một thiên đường giữa sa mạc mênh\r\nmông bao la cát gió. Cách thành phố Đôn Hoàng, thuộc tỉnh Cam Túc Trung Quốc 5km về phía tây nam. Nguyệt Nha\r\nTuyền không biết từ bao giờ nguy nga hiện hữu. Như hình ảnh phản chiếu của vầng trăng lưỡi liềm chưa tròn\r\ntháng xuống trái đất, như miền cực lạc loài người giữa sa mạc nắng cháy.', NULL, NULL);
INSERT INTO `tips` VALUES (37205, 'Chuyển chú là mượn từ chữ có sẵn, đem thay hình đổi dạng thành chữ khác, nhưng\r\ncó nghĩa tương cận (gần gũi)', NULL, NULL);
INSERT INTO `tips` VALUES (37206, 'Đánh giá về khả năng miêu tả chi tiết, khả năng liên tưởng của thí\r\nsinh.', NULL, NULL);
INSERT INTO `tips` VALUES (37207, 'Đánh giá, cho điểm thí sinh dựa theo tiêu chí dưới đây:\r\n- Thi sinh sẽ đạt điểm số cao khi có thể trả lời câu hỏi chính xác, súc tích.\r\n- Thí sinh sẽ đạt điểm số trung bình khi có thể trả lời câu hỏi chính xác nhưng bị ngắt\r\nquãng, lặp lại hoặc sai ngữ pháp.\r\n- Thí sinh sẽ đạt điểm số thấp khi nội dung trả lời không rõ ràng, không đúng trọng tâm.', NULL, NULL);
INSERT INTO `tips` VALUES (37208, 'Khi đọc năm, ta đọc lần lượt từng số sau đó thêm 年 (nián) vào cuối.', NULL, NULL);
INSERT INTO `tips` VALUES (37209, '冤种 (大冤种)\r\nCó nguồn gốc từ tiếng địa phương vùng Đông Bắc. “冤种” có nghĩa là người cảm thấy buồn bã vì bị đổ oan, sau\r\ntrở nên phổ biến trên mạng xã hội. Hiện, thường được dùng để chỉ khi làm việc dại thường sẽ bị gọi là\r\n“纯纯大冤种”.', NULL, NULL);
INSERT INTO `tips` VALUES (37210, 'Có một số quy tắc biến điệu mà bạn luôn phải ghi nhớ như: hai thanh 3 liền nhau\r\nthì thanh 3 thứ nhất trở thành thanh 2.', NULL, NULL);
INSERT INTO `tips` VALUES (37211, 'Để gây ấn tượng tốt với giám thị chấm thi bạn có thể mở lời xin trả lời\r\ncâu hỏi 5, và cảm ơn khi kết thúc câu hỏi số 6 ta có thể sử dụng mẫu câu tham khảo sau:\r\n- 尊敬的各位老师，您好。题目已收到，我开始答题。(Zūnjìng de gèwèi lǎoshī, nín hǎo. Tímù yǐ shōu dào, wǒ kāishǐ dátí.)\r\n- 答题完毕，感谢各位老师。(Dátí wánbì, gǎnxiè gèwèi lǎoshī.)', NULL, NULL);
INSERT INTO `tips` VALUES (37212, '你礼貌吗？\r\nDùng để trả lời khi bị hỏi những câu hỏi bất lịch sự mà đáng nhẽ không nên hỏi. Tương đương với câu \'Hỏi gì\r\nhỏi vô duyên vậy?\', \'Có tý miếng duyên nào không?\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37213, 'Thư chấp thuận là gì?\r\nThư chấp thuận của giáo sư là một lá thư hoặc biểu mẫu do giáo sư của trường mà bạn đăng ký viết, thể hiện\r\nrằng rằng thầy, cô sẵn sàng chấp nhận bạn làm nghiên cứu sinh thạc sĩ/tiến sĩ của thầy, cô sau khi bạn nhập\r\nhọc tại trường.', NULL, NULL);
INSERT INTO `tips` VALUES (37214, 'Tại sao trong nhà lại trưng bày CÓC VÀNG NGẬM TIỀN?\r\nXuất phát từ cụm 金钱缠身 (nghĩa là tiền bạc xông xênh) đồng âm với 金蝉 (nghĩa là con cóc), cũng bởi vì vậy mà\r\nnhiều nhà trưng bày con cóc vàng ngậm tiền cầu cho năm mới tiền bạc rủng rỉnh.', NULL, NULL);
INSERT INTO `tips` VALUES (37215, 'PUA (CPU/ KTV/ PPT/ ICU)\r\nCâu đùa này xuất phát từ một cuộc phỏng vấn với Xu Di (许娣), khi đối mặt với câu hỏi của phóng viên: “Nên làm\r\ngì khi bạn trai nói mình không đẹp?” Xu Di ban đầu muốn trả lời là “Anh ấy đang PUA (thao túng tâm lý) bạn”\r\nnhưng cô ấy lại nói thành: “Anh ấy đang CPU bạn”. Cư dân mạng xứ tỷ dân đã tận dụng cơ hội để điều chỉnh\r\nthành CPU và “chế” các meme. Hay thậm chí thay đổi PUA thành bất kỳ các từ có ba chữ cái, ví dụ như: ICU,\r\nKTV, KFC,…', NULL, NULL);
INSERT INTO `tips` VALUES (37216, 'Bài thi HSKK CC gồm ba phần và tổng cộng 6 câu hỏi:\r\n- Phần 1: Nghe và nhắc lại\r\n- Phần 2: Đọc to\r\n- Phần 3: Trả lời câu hỏi', NULL, NULL);
INSERT INTO `tips` VALUES (37217, 'Tứ đại danh sơn Trung Quốc gồm Núi Ngũ Đà, núi Cửu Hoa, núi Nga Mi và núi Phổ\r\nĐà.', NULL, NULL);
INSERT INTO `tips` VALUES (37218, 'Tứ đại gian thần Trung Quốc:\r\nSái Kinh – 蔡京 (1047-1126)\r\nTần Cối – 秦桧 – (1091 – 1155)\r\nHòa Thân – 和珅 (1750 – 1799)\r\nNghiêm Tung – 严嵩 (1480-1567)', NULL, NULL);
INSERT INTO `tips` VALUES (37219, 'Phần 1 nghe và lặp lại câu đơn, bởi câu chỉ từ 5 - 9 chữ, chứa 2-3\r\nthông tin nên yêu cầu thí sinh khi nhắc lại cần nhắc lại đúng, chính xác câu vừa được nghe.', NULL, NULL);
INSERT INTO `tips` VALUES (37220, 'Cụm từ 戴绿帽 (dàilǜ mào), có nghĩa đen là “đội mũ xanh”, có nghĩa là vợ của một\r\nngười đàn ông đang lừa dối anh ta, vì vậy bạn không bao giờ nên cho một người đàn ông Trung Quốc đội chiếc\r\nmũ xanh.', NULL, NULL);
INSERT INTO `tips` VALUES (37221, '“老六” là một từ xuất hiện trong trò chơi bắn súng, mang nghĩa xấu. Các trận chiến\r\ntrong game thường là “năm chọi năm” nhưng có một số người không hợp tác. Trong khi đồng đội đang “chiến đấu”\r\nthì những “老六” này ẩn mình và giành chiến thắng một cách không thuyết phục. “服了你个老六” là cụm từ phổ biến được\r\nsử dụng bởi các game thủ.\r\nNgoài trong game ra, câu nói này chủ yếu được sử dụng trong giao tiếp hàng ngày để chế giễu những người\r\nchiến thắng một cách bất ngờ thông qua những việc làm xảo quyệt.', NULL, NULL);
INSERT INTO `tips` VALUES (37222, 'Chữ hình thanh chiếm khoảng 70% chữ Hán.', NULL, NULL);
INSERT INTO `tips` VALUES (37223, '我们 (wǒmen) và 咱们 (zánmen) đều có nghĩa là \'chúng ta, chúng tôi\'. Tuy nhiên, 我们\r\n(wǒmen) bao gồm người nói và người nghe (bao gồm cả người đang không có mặt ở đó), 咱们 (zánmen) chỉ bao gồm\r\ncả người nói và người nghe.', NULL, NULL);
INSERT INTO `tips` VALUES (37224, 'Bạn có bao giờ đặt câu hỏi: “tại sao là cùng là 武 nhưng 漢武帝/Hàn wǔdì/ lại gọi là\r\nHán Vũ Đế mà không phải Hán Võ Đế, 武則天/Wǔzétiān/ là Võ Tắc Thiên chứ không phải Vũ Tắc Thiên?” hay không?', NULL, NULL);
INSERT INTO `tips` VALUES (37225, 'Cuộc thi DỊCH NÓI GIỎI được tổ chức hàng năm bởi Viện Khổng Tử thuộc Trường Đại\r\nhọc Hà Nội, dành riêng cho sinh viên ở miền Bắc. Tham gia DỊCH NÓI GIỎI không chỉ giúp bạn thể hiện trình độ\r\nngoại ngữ và khả năng phiên dịch, mà còn là cơ hội tuyệt vời để tiếp xúc trực tiếp với các nhà tuyển dụng,\r\nmở ra những cánh cửa mới trong sự nghiệp của bạn.', NULL, NULL);
INSERT INTO `tips` VALUES (37226, 'Ngoài ra, các giải thưởng giá trị có thể lên đến hàng chục triệu đồng và cơ hội nhận được chứng chỉ cao quý\r\ntừ Viện Khổng Tử đang chờ đợi các thí sinh.', NULL, NULL);
INSERT INTO `tips` VALUES (37227, 'Phó từ chỉ trình độ 太 (tài) biểu thị trình độ cực kì cao, cuối câu thường có 了.', NULL, NULL);
INSERT INTO `tips` VALUES (37228, 'Khi các bạn đang phân tích kết cấu chữ Hán, hãy gọi thành phần cấu tạo nên chữ\r\nHán là thiên bàng. Còn khi đang tra từ điển thì hãy gọi nó là bộ thủ.', NULL, NULL);
INSERT INTO `tips` VALUES (37229, 'Đọc thơ đoán từ vựng:\r\n\'Tựa cây (木) mỏi mắt (目) chờ mong\r\nNgười nơi xa ấy trong lòng (心) có hay?\r\nĐây là chữ gì?', NULL, NULL);
INSERT INTO `tips` VALUES (37230, 'Người Trung Quốc kiêng tặng hoa cúc vì hoa cúc được gắn với những dịp như đám\r\ntang hoặc lễ tang cho người đã khuất. Nếu bạn đến thăm một người bạn với hoa cúc trong dịp Tết Nguyên đán,\r\nchắc chắn bạn sẽ bị đuổi khỏi nhà của họ.', NULL, NULL);
INSERT INTO `tips` VALUES (37231, 'Hán phục là y phục và trang sức truyền thống của dân tộc Hán, Trung Hoa. Nó còn\r\nđược gọi là Hán trang, Hoa phục. Nó có nguồn gốc từ thời Tam Hoàng Ngũ Đế.', NULL, NULL);
INSERT INTO `tips` VALUES (37232, '栓Q\r\nTừ “栓Q” bắt nguồn từ Liu Tao (刘涛), một nông dân sinh năm 1970 ở huyện Dương Sóc, Quế Lâm, Quảng Tây. Ông\r\ngiới thiệu cảnh đẹp ở huyện Dương Sóc bằng tiếng Trung và tiếng Anh, sau khi hát ca khúc “Row row row your\r\nboat”, ông kết thúc bằng lời cảm ơn. Cách phát âm hài hước từ “thank you” đã thu hút sự chú ý của cư dân\r\nmạng và nhanh chóng trở nên phổ biến trên Internet.', NULL, NULL);
INSERT INTO `tips` VALUES (37233, '“嘴替” chỉ một người có thể thay tiếng nói của đại đa số cư dân mạng. Mặc dù cư\r\ndân mạng có ý kiến ​​và thái độ về một số vấn đề, nhưng họ thường không truyền đạt được suy nghĩ của mình vì\r\nkhả năng diễn đạt không tốt. Do đó, có một số người giỏi nắm bắt, tóm tắt những suy nghĩ chung của một nhóm\r\nhoặc một vài người nhất định và có thể diễn đạt chúng một cách ngắn gọn để khơi dậy sự đồng tình của mọi\r\nngười. Vì vậy, cư dân mạng đã dùng từ “嘴替” để thể hiện sự khẳng định trước những nhận xét tuyệt vời này và\r\ndành lời khen cho những ai dám nói ra suy nghĩ của mình.', NULL, NULL);
INSERT INTO `tips` VALUES (37234, 'Vè đọc nhịu (3):\r\n吃葡萄不吐葡萄皮儿\r\n不吃葡萄倒吐葡萄皮儿\r\nPinyin:\r\nChī pútáo bù tǔ pútáo pír\r\nbù chī pútáo dào tǔ pútáo pír', NULL, NULL);
INSERT INTO `tips` VALUES (37235, 'Bốn huyền thoại Trung Quốc gồm: Hậu Nghệ bắn hạ mặt trời, Hằng Nga lên mặt\r\ntrăng, Nữ Oa bầu trời và Cộng Công húc đổ núi Bất Chu', NULL, NULL);
INSERT INTO `tips` VALUES (37236, 'Theo nghiên cứu cho thấy não bộ thức nhiều nhất vào 4 thời điểm trong ngày. Đây\r\ncũng là khoảng thời gian học tập hiệu quả. Nếu bạn sắp xếp được khoảng thời gian này hợp lý thì việc học\r\ntập, nắm vững kiến thức ngoại ngữ sẽ vô cùng hiệu quả.', NULL, NULL);
INSERT INTO `tips` VALUES (37237, 'Tết Nguyên Đán tiếng Trung là 春节 (chūn jié), đừng nhầm thành 元旦节 (Tết dương\r\nlịch) nha.', NULL, NULL);
INSERT INTO `tips` VALUES (37238, '看 là nhìn (nhấn mạnh hành động nhìn), 看见 là \'nhìn thấy\' (nhấn mạnh kết quả của\r\nđộng tác nhìn).', NULL, NULL);
INSERT INTO `tips` VALUES (37239, 'Nghệ thuật cắt giấy có lịch sử từ thời Tây Hán (TCN). Những hoa văn ban đầu\r\nthường được cắt trên da động vật, mảnh đồng, bạc vàng dát mỏng.', NULL, NULL);
INSERT INTO `tips` VALUES (37240, 'Chứng chỉ HSKK có giá trị 2 năm kể từ ngày cấp, điểm thi HSKK được ghi chung với\r\nđiểm HSK.', NULL, NULL);
INSERT INTO `tips` VALUES (37241, 'Vì sao có tên gọi là tết Trùng Cửu?\r\nCon số 9 được coi là số dương, sự lặp lại hai lần số 9 nên gọi Trùng Cửu, Trùng Dương là vì vậy. Tên tiếng\r\nTrung tết Trùng Cửu là 重九, tên tiếng Trung tết Trùng Dương là 重阳 .', NULL, NULL);
INSERT INTO `tips` VALUES (37242, 'Pinyin được phê chuẩn năm 1958 và được áp dụng năm 1979 tại Cộng hòa Nhân dân\r\nTrung Hoa.', NULL, NULL);
INSERT INTO `tips` VALUES (37243, 'Muốn nói tốt, bạn cần phải rèn khả năng nghe tốt trước.\r\n\'疯狂暗示\' ngôn ngữ mạng \'phát vô số tín hiệu\'', NULL, NULL);
INSERT INTO `tips` VALUES (37244, 'HSK là thi khảo sát trình độ tiếng Hán của những người không sử dụng tiếng Hán\r\nlà ngôn ngữ chính.', NULL, NULL);
INSERT INTO `tips` VALUES (37245, 'Trong tiếng Trung, một số động từ có thể lặp lại biểu thị sự \'ngắn ngủi, nhất\r\nthời\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37246, 'Người Trung Quốc ăn gì vào tết Trùng Dương?\r\nVào dịp tết Trùng Cửu, người ta còn làm loại bánh mang tên ‘bánh Trùng Cửu’. Loại bánh này bắt nguồn từ\r\nnhững nơi không có núi. Trong tiếng Hán, bánh điểm tâm(糕点) có cách đọc gần giống với “cao điểm” – trong đó,\r\n“糕 (cao)” nghĩa là bánh. Chữ “糕 (cao)” này phát âm trùng với chữ “高” trong từ “登高 (đăng cao)”, có nghĩa là\r\nlên cao. Vì vậy, mọi người cho rằng, ăn bánh Trùng Cửu còn có thể thay thế cho việc lên núi cao.', NULL, NULL);
INSERT INTO `tips` VALUES (37247, 'Thay vì quá chú tâm vào ngữ pháp, bạn hãy nghe đối thoại và lặp lại liên tục các\r\ncâu ví dụ nhé.', NULL, NULL);
INSERT INTO `tips` VALUES (37248, 'Âm tiết thứ 2 của những động từ lặp lại hoặc từ láy thì chúng ta đọc thanh nhẹ.\r\nVí dụ: 弟弟 (dìdi) em trai', NULL, NULL);
INSERT INTO `tips` VALUES (37249, '例题: 别人都夸我( )，其实我( )\r\n小朋友写: 别人都夸我( 很帅 )，其实我( 是戴面具的 )。\r\n老师评语: 什么面具这么好用???', NULL, NULL);
INSERT INTO `tips` VALUES (37250, '不管别人怎么看你，你自己都要看得起自己 (bù quǎn bié rén zěn me kàn nǐ , nǐ zì jǐ dōu yào kàn de qǐ\r\nzì jǐ)\r\nBất kể người khác nghĩ gì về bạn, tự bản thân bạn phải biết coi trọng bản thân mình.\r\nĐây là câu dùng để khích lệ đối phương có dũng khí làm một việc gì đó , không vì những lời nghị luận hoặc\r\nsuy nghĩ của người khác mà không dám làm.', NULL, NULL);
INSERT INTO `tips` VALUES (37251, 'Thành phố Lhasa, thủ phủ của khu tự trị Tây Tạng, Trung Quốc, là một trong những\r\nđiểm đến mà biết bao người Trung Quốc ao ước được một lần đặt chân đến nơi đây. Mỗi năm, thành phố Lhasa thu\r\nhút tới hơn 30 triệu lượt khách tham quan trong và ngoài Trung Quốc.', NULL, NULL);
INSERT INTO `tips` VALUES (37252, 'Trước kia, cô dâu Trung Quốc thường mặc trang phục xường xám màu đỏ\r\ntrong ngày cưới.\r\n\'蛮横无理\' ngang như cua.', NULL, NULL);
INSERT INTO `tips` VALUES (37253, 'Sử dụng thời gian rảnh để học một chút, dần dần bạn sẽ thấy tiến bộ của mình.', NULL, NULL);
INSERT INTO `tips` VALUES (37254, 'Châm cứu là di sản quý giá của khoa học truyền thống và văn hóa dân tộc Hán.\r\nLiệu pháp châm cứu là một bộ phận của di sản y học Trung Hoa, và cũng là một loại thủ pháp trị bệnh mà chỉ\r\nngười Trung Hoa mới có. Sau này châm cứu được phổ biến truyền rộng rãi đến các nước khác trên thế giới.', NULL, NULL);
INSERT INTO `tips` VALUES (37255, 'Nguyên âm “ü” chỉ có thể ghép với 5 phụ âm “n”, “l”, “j”, “q”, “x”.', NULL, NULL);
INSERT INTO `tips` VALUES (37256, 'Học bổng ASEAN là học bổng được Bộ Giáo dục Trung Quốc cung cấp cho các bạn có\r\nthành tích tiếng Trung và thành tích học tập tốt, trong khu vực các nước ASEAN.\r\nTiêu chuẩn:\r\n- Miễn học phí, ký túc xá, phí bảo hiểm y tế\r\n- Cung cấp sinh hoạt phí (1000-2500 tệ/tháng tùy bậc học)\r\nYêu cầu: Yêu cầu tối thiểu có HSK 4, GPA 7.0 trở lên', NULL, NULL);
INSERT INTO `tips` VALUES (37257, 'Hồ sơ xin học bổng CSC Trung Quốc\r\n1. Đơn xin học bổng\r\n2. Đơn xin học bổng từ trường\r\n3. Ảnh 4×6 nền trắng: 4 ảnh\r\n4. Bằng tốt nghiệp (Bản dịch công chứng)\r\n5. Bảng điểm (học bạ, bản dịch công chứng)\r\n6. Kế hoạch học tập (tiếng Anh hoặc tiếng Trung)\r\n7. Thư giới thiệu\r\n8. Giấy khám sức khỏe mẫu Trung Quốc\r\nLưu ý:\r\n- Tất cả giấy tờ phải là tiếng Trung hoặc tiếng Anh vì thể cần đưa đi dịch thuật công chứng, nếu là giấy tờ\r\nsong ngữ thì không cần.\r\n- Bộ hồ sơ gửi cho trường bao gồm 2 bộ: 1 bộ trường giữ, 1 bộ gửi lên CSC.', NULL, NULL);
INSERT INTO `tips` VALUES (37258, 'Hướng dẫn sử dụng: tìm MẸO hán tự:\r\n1. Chọn mục \'Hán tự\'\r\n2. Mẹo Hán tự bên dưới.', NULL, NULL);
INSERT INTO `tips` VALUES (37259, 'Đọc câu đố đoán từ vựng:\r\n五个兄弟\r\nwǔ gè xiōngdì\r\n住在一起\r\nzhù zài yīqǐ\r\n名字不同\r\nmíngzì bùtóng\r\n高矮不齐。\r\ngāo\'ǎi bù qí.', NULL, NULL);
INSERT INTO `tips` VALUES (37260, 'Họ 武 trong tiếng Trung khi dịch ra Tiếng Việt thì lại cũng có hai cách dịch Vũ\r\nvà Võ.', NULL, NULL);
INSERT INTO `tips` VALUES (37261, 'Đọc thơ đoán từ vựng:\r\n\'Trăng (月) kia khéo ghép lên hai mảnh\r\nTạo nên son sắc nghĩa kim BẰNG.\'\r\nĐây là chữ gì?', NULL, NULL);
INSERT INTO `tips` VALUES (37262, 'Người Trung Quốc có xu hướng đặt lưỡi giữa hai răng, khi nói chuyện lưỡi sẽ hoạt\r\nđộng với hướng lên cao và chạm tới khoang miệng ở hàm trên.', NULL, NULL);
INSERT INTO `tips` VALUES (37263, 'Triển tý vè đọc nhịu (2):\r\n牛郎恋刘娘\r\n刘娘念牛郎\r\n牛郎年年恋刘娘\r\n刘娘年年念牛郎\r\nPinyin:\r\nNiú láng liàn liú niáng\r\nliú niáng niàn niú láng\r\nniú láng nián nián liàn liú niáng\r\nliú niáng nián nián niàn niú láng', NULL, NULL);
INSERT INTO `tips` VALUES (37264, 'Đừng quên việc luyện tập.', NULL, NULL);
INSERT INTO `tips` VALUES (37265, 'Chữ \'看\' có hai âm đọc là \'kàn\' và \'kān\', chỉ đọc là \'kān\' khi mang\r\nnghĩa \'trông, coi\':\r\n- 看家\r\n- 看门\r\n- 看守\r\n- 看管', NULL, NULL);
INSERT INTO `tips` VALUES (37266, 'Trong cuộc sống thường ngày chúng ta có thể thay thế “你好“ bằng 哈喽，嗨，嘿.', NULL, NULL);
INSERT INTO `tips` VALUES (37267, 'Món Phật nhảy tường có nguồn gốc từ Phúc Kiến, Trung Quốc và đã có lịch sử kéo\r\ndài hơn 200 năm. Đây là một món ăn độc đáo và đặc biệt, sử dụng những nguyên liệu quý hiếm như hải sâm, bào\r\nngư, và vi cá mập. Chỉ từ những nguyên liệu này, món Phật nhảy tường đã tạo nên một hương vị độc đáo và\r\nphong phú. Khi nghe về món này, không ai có thể cưỡng lại sự hấp dẫn của nó.', NULL, NULL);
INSERT INTO `tips` VALUES (37268, '老师：用“一……就……”造几个句子给我听。\r\n甲：天气一变冷我就加衣服。\r\n乙：甲一加衣服我就知道天气变冷了。\r\n丙：天气一变冷甲就加衣服。\r\n老师：……?', NULL, NULL);
INSERT INTO `tips` VALUES (37269, 'Quả lê trong tiếng Trung đồng nghĩa với từ “li biệt”. Bởi vậy, người Trung Quốc\r\nquan niệm sẽ không ăn chung với người khác một trái lê, tránh những điều kém may mắn xảy ra.', NULL, NULL);
INSERT INTO `tips` VALUES (37270, '谁 có hai phiên âm là \'shéi\' và \'shuí\'.\r\n\'万事开头难\' Trong cuộc sống, những điều không thuận ý vẫn luôn tồn tại, vì vậy nếu\r\nbạn có khởi đầu không tốt thì cũng đừng nản lòng. Dần dần mọi khó khăn cũng biến thành trái ngọt nếu như bạn\r\nkiên trì theo đuổi đến cùng.', NULL, NULL);
INSERT INTO `tips` VALUES (37271, 'Hứa Thận soạn Thuyết văn giải tự qua việc phân tích chữ triện (đặc biệt là loại\r\ntiểu triện 小篆) được hình thành chậm rãi và có hệ thống từ giữa đến cuối thời Nhà Chu trong lịch sử nước Tần,\r\nrồi được tiêu chuẩn hóa và thống nhất lại trong thời Nhà Tần và được truyền bá trên cả nước.', NULL, NULL);
INSERT INTO `tips` VALUES (37272, '怎么 (zěnme) luôn đứng trước động từ trong câu dùng để hỏi phương thức và cách\r\ndiễn ra hành động đó.', NULL, NULL);
INSERT INTO `tips` VALUES (37273, 'Trong Tiếng Hán, họ đặt trước, tên đặt sau.', NULL, NULL);
INSERT INTO `tips` VALUES (37274, 'Cách hỏi tên\r\nKhi muốn hỏi tên ai đó, có thể sử dụng mẫu câu sau \'你叫什么名字?\'\r\nCách hỏi này mang ngữ khí tự nhiên, thường dùng trong các trường hợp\r\nxã giao thông thường của những người bằng tuổi, vai vế ngang nhau, ...', NULL, NULL);
INSERT INTO `tips` VALUES (37275, 'Từ vựng được dùng phổ biến khi chào hỏi:\r\n- 嗨，哈喽，早，你好，您好，你们好，大家好.', NULL, NULL);
INSERT INTO `tips` VALUES (37276, 'Thiên bàng là gì?\r\nThiên bàng là bộ phận cấu thành nên chữ Hán (loại chữ hình thanh), được dùng để phân tích hình thể của con\r\nchữ trong tiếng Trung, từ đó ta phát hiện được nghĩa của chữ và âm đọc của chữ.\r\nThiên bàng có hai loại là thiên bàng biểu ý, còn gọi là hình bàng và thiên bàng biểu âm, gọi là thanh bàng.\r\nHình bàng và thanh bàng hợp lại sẽ tạo nên chữ hợp thể (合体字). Điều đó có nghĩa, thiên bàng chỉ xuất hiện\r\ntrong chữ hợp thể, không bao giờ xuất hiện trong chữ đơn thể, lại càng không có chuyện được tạo nên từ chỉ\r\nmột nét bút.\r\nVí dụ:\r\n- Chữ 湖: Hình bàng là 氵, thanh bàng là 胡\r\n- Chữ 晴: Hình bàng là 日, thanh bàng là 青\r\nLưu ý:\r\n- Bộ thủ có thể là thiên bàng, nhưng thiên bàng chưa chắc là bộ thủ.', NULL, NULL);
INSERT INTO `tips` VALUES (37277, 'Cách trả lời khi được hỏi tên\r\nKhi trả lời có thể sử dụng các cách sau:\r\n我叫 ...... (có thể nói cả họ và tên hoặc chỉ nói tên)\r\n我姓 ...... (thường nói họ)\r\n我是 ...... (có thể nói cả họ và tên hoặc chỉ nói tên)\r\nVí dụ:\r\n我姓林。 (Wǒ xìng Lín.)\r\nTôi họ Lâm.\r\n我叫芳鸳。 (Wǒ jiào Fāng yuān)\r\nTôi là Phương Uyên.\r\n我是马克。 (Wǒ shì Mǎkè.)\r\nTôi là Mark.', NULL, NULL);
INSERT INTO `tips` VALUES (37278, 'Từ vựng chỉ nhớ khi nó được lặp lại nhiều lần, đặc biệt dễ nhớ khi có ngữ cảnh\r\nhay đặt trong văn cảnh cụ thể.', NULL, NULL);
INSERT INTO `tips` VALUES (37279, 'Vì Giáp cốt văn thường dùng vào việc ghi chép những điều bói toán\r\nlà chính nên còn gọi là \'bốc tử\' hoặc \'khế văn\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37280, 'HSKK sơ cấp tương ứng cấp độ 1, 2 của “Thang điểm năng lực tiếng Trung” và cấp A\r\ncủa Khung Tham chiếu Ngôn ngữ Chung Châu Âu (CEF). Thí sinh vượt qua kỳ thi HSKK sơ cấp có thể diễn đạt\r\ntiếng Trung quen thuộc hơn với chủ đề khẩu ngữ hàng ngày đáp ứng nhu cầu giao tiếp cơ bản.', NULL, NULL);
INSERT INTO `tips` VALUES (37281, 'Đọc câu đố đoán từ vựng:\r\n屋子方方，有门没窗，屋外热烘，屋里冰霜。\r\nWūzi fāng fāng, yǒu mén méi chuāng, wūwài rè hōng, wū li bīngshuāng.', NULL, NULL);
INSERT INTO `tips` VALUES (37282, 'Hội ý là hợp ý của từng phần lại để hình thành nghĩa mới.\r\nVí dụ:信 Tín; 林 Lâm. v.v…', NULL, NULL);
INSERT INTO `tips` VALUES (37283, 'Quy tắc trên trước dưới sau:\r\nHầu hết các chữ đều viết từ trên xuống dưới, ví dụ chữ “二 nhị” là số 2 gồm 2 nét ngang , sẽ viết từ trên\r\nxuống。', NULL, NULL);
INSERT INTO `tips` VALUES (37284, 'Người Trung Quốc kiêng tặng lê vì giống như ô, đồng âm của quả lê (梨) và phân ly\r\n(分离) ám chỉ sự xa cách vĩnh viễn của gia đình hoặc bạn bè, không phù hợp với không khí vui tươi trong lễ\r\nhội. Vì vậy, khi chọn quà cho người yêu hay bố mẹ thì nên tránh những quả lê.', NULL, NULL);
INSERT INTO `tips` VALUES (37285, 'Cuộc thi TÔI LÀ DIỄN THUYẾT GIA do CLB Tiếng Trung Hanu - Hanu Chinese Club tổ\r\nchức, nhằm hướng đến học sinh và sinh viên yêu thích tiếng Trung ở miền Bắc.', NULL, NULL);
INSERT INTO `tips` VALUES (37286, 'Top 20 thí sinh xuất sắc của cuộc thi sẽ được trao tặng chứng nhận từ khoa Trung Hanu và có cơ hội nhận\r\nnhiều giải thưởng giá trị, bao gồm tiền mặt, suất học bổng du học trực tuyến, gói hỗ trợ apply học bổng\r\nTrung Quốc, khóa học tại trung tâm Hán ngữ YOYO, khóa học luyện giọng cơ bản tại Trung tâm Mc song ngữ\r\nRoseway và hàng ngàn phần quà khác.', NULL, NULL);
INSERT INTO `tips` VALUES (37287, 'Cuộc thi hứa hẹn sẽ mang lại cho các bạn học sinh, sinh viên một sân chơi giao lưu tiếng Trung thú vị và bổ\r\ních, giúp tăng cường kỹ năng diễn thuyết và tư duy phản biện.', NULL, NULL);
INSERT INTO `tips` VALUES (37288, 'Nói thật nhiều và đừng sợ mắc lỗi.', NULL, NULL);
INSERT INTO `tips` VALUES (37289, 'Chứng chỉ TOCFL là gì?\r\nĐây là chứng chỉ năng lực Tiếng Hoa, được sử dụng để đánh giá khả năng ngoại ngữ của những người ngôn ngữ mẹ\r\nđẻ không phải Tiếng Hoa do Đài Loan tổ chức. Được chia ra làm 3 band và 6 cấp độ chính.\r\nMỗi band bao gồm 100 câu trắc nghiệm nghe và đọc hiểu, thời gian thi của mỗi band là 120 phút. Có hai phần\r\nchính là nghe và đọc hiểu. Cấu trúc TOCFL nghe gồm: Xem tranh trả lời câu hỏi, đối thoại ngắn, đối thoại\r\ndài, đoạn văn Thời gian làm bài là 60 phút. Cấu trúc TOCFL đọc hiểu gồm: câu đơn, xem tranh giải thích, hoàn\r\nthành đoạn văn, điền vào chỗ trống, đọc hiểu đoạn văn. Thời gian thi cũng là 60 phút..\r\nThi TOCFL có thể chọn hình thức thi giản thể và phồn thể, hầu hết là thi trên máy tính. Đúng như nơi cấp là\r\nĐài Loan thì để xin được học bổng của Đài Loan bạn phải cần chứng chỉ TOCFL này, không chấp nhận HSK\r\nPhong tục kết hôn với người chết, hay còn gọi là minh hôn (hôn âm), đã xuất hiện\r\ntừ thế kỷ 17 trước Công nguyên tại Trung Quốc. Tưởng nhớ đến một câu chuyện dân gian, người Trung Quốc thực\r\nhiện hôn lễ với người đã qua đời để đáp ứng mong muốn của người thân. Phong tục này từng đạt đến độ phổ biến\r\ntrong thời nhà Tống và bị cấm chính thức vào năm 1949.', NULL, NULL);
INSERT INTO `tips` VALUES (37290, 'Chỉ sự là phép chỉ vào sự vật mà viết ra chữ, nhìn mà xét ra ý.\r\nVí dụ:上 Thượng; 下 Hạ; 本 Bản ; 末 Mạt v.v...', NULL, NULL);
INSERT INTO `tips` VALUES (37291, '\'糊了\' ngôn ngữ mạng \'bóc phốt nhẹ\'', NULL, NULL);
INSERT INTO `tips` VALUES (37292, '元旦 cách đọc yuándàn, tuy nghe rất giống Nguyên đán nhưng lại là tết Dương lịch.', NULL, NULL);
INSERT INTO `tips` VALUES (37293, 'Những thí sinh đăng ký HSK5 và HSK6 sẽ bắt buộc phải đăng ký HSKK CC.', NULL, NULL);
INSERT INTO `tips` VALUES (37294, '我想在你的青春里留下我的名字.\r\nWǒ xiǎng zài nǐ de qīngchūn lǐ liú xià wǒ de míngzi.\r\nAnh muốn trong thanh xuân của em lưu giữ tên anh.\r\n\'摸头杀\' ngôn ngữ mạng \'chết lịm trong cái xoa đầu của bạn trai\'', NULL, NULL);
INSERT INTO `tips` VALUES (37295, 'Huyền táng - trong đó chữ “huyền” có nghĩa là treo, tức là treo quan tài của\r\nngười chết trên các vách núi dựng đứng cực kỳ nguy hiểm. Người ta cho rằng vách núi hay hang động trên cao\r\nlà nơi yên tĩnh, thích hợp để linh hồn yên nghỉ. Từ trên cao, người chết có thể ngắm nhìn trời xanh, sông\r\nnúi, cách biệt với sự ồn ào của nhân gian. Vì vậy, một số địa phương chỉ còn gọi nơi huyền táng là “tiên\r\nhàm”, “tiên thất”, “tiên đài”.', NULL, NULL);
INSERT INTO `tips` VALUES (37296, 'Học viết kết hợp học bổ thủ và Hán Việt sẽ giúp ích rất lớn cho các bạn.\r\n\'嘴硬\' ngôn ngữ mạng \'cãi cùn, cãi cố, cứng miệng\'', NULL, NULL);
INSERT INTO `tips` VALUES (37297, 'Học bổng Trần Gia Canh\r\nHọc bổng Trần Gia Canh (đại học Hoa Kiều) được Tổ chức giáo dục Hạ Môn Trần Gia Canh thành lập, tạo cơ hội\r\ndu học cho các bạn học sinh/sinh viên muốn đến thành phố Hạ Môn học tập.\r\nTiêu chuẩn:\r\n- Miễn học phí, ký túc xá, phí bảo hiểm y tế\r\n- Cung cấp sinh hoạt phí (1000-3000 tệ/tháng tùy bậc học)\r\nYêu cầu: Tùy theo yêu cầu trường mà bạn ứng tuyển.', NULL, NULL);
INSERT INTO `tips` VALUES (37298, 'Hai thanh 3 khi đi cùng nhau thanh 3 đầu tiên sẽ biến thành thanh 2.', NULL, NULL);
INSERT INTO `tips` VALUES (37299, 'Bộ thủ bắt nguồn từ thiên bàng, nói một cách thông tục, dân dã thì\r\nthiên bàng chính là cha sinh mẹ đẻ của bộ thủ.', NULL, NULL);
INSERT INTO `tips` VALUES (37300, 'Các bước để có được thư chấp thuận từ giáo sư\r\nBước 1: Tìm thông tin giáo sư\r\nBạn có thể tìm thấy danh sách giảng viên trên website của trường, ở đó có đầy đủ thông tin liên hệ cũng như\r\ncác đề tài mà giảng viên từng nghiên cứu. Lưu ý hãy tìm giảng viên đúng chuyên ngành và phù hợp với phương\r\nhướng nghiên cứu của bạn trong tương lai.\r\nBước 2: Chuẩn bị hồ sơ\r\n- Thư chấp thuận\r\n- Bằng tốt nghiệp THPT hoặc Đại học\r\n- Bảng điểm\r\n- Kế hoạch học tập\r\n- Chứng chỉ tiếng Trung\r\n- Chứng nhận tham gia hoạt động ngoại khóa, thành tích khác\r\nBước 3: Liên hệ với giáo sư\r\nGửi email cho giáo sư mà bạn muốn xin thư chấp thuận cùng các tài liệu bạn đã chuẩn bị và nêu rõ mục đích\r\ncủa bạn trong email:\r\n– Tại sao bạn muốn theo học tại trường đại học này?\r\n– Tại sao lĩnh vực nghiên cứu của giáo sư sẽ phù hợp với mục tiêu nghiên cứu của bạn?', NULL, NULL);
INSERT INTO `tips` VALUES (37301, '退！退！退！\r\nMột đoạn video đã ghi lại cuộc cãi vã giữa một chủ xe và một bác bán hàng quán để giành chỗ đậu xe. Trong\r\nvideo, bác gái không có phản ứng tích cực trước những chất vấn của người khác, vừa giậm chân vừa khua tay\r\nmúa kiếm. Đồng thời hét lớn “退！退！退 (Lùi! Lùi! Lùi!)”. Bác gái vừa nói vừa làm động tác đặc biệt giống với\r\nhành động xua đuổi tà ma trong phong tục dân gian truyền thống. Hành động hài hước này đã gây hiệu ứng mạnh\r\nvà tạo ra “trend” mới, được lan truyền rộng rãi trên Internet.', NULL, NULL);
INSERT INTO `tips` VALUES (37302, 'Phần thi sắp xếp HSK4 và HSK5 khi thi trên máy tính sẽ KÉO chữ nhaa.', NULL, NULL);
INSERT INTO `tips` VALUES (37303, 'HSK cũ có 11 cấp độ. Hiện nay chỉ còn Chứng chỉ HSK còn 6 cấp độ chia làm 3\r\nloại: Sơ cấp, Trung cấp và Cao cấp.\r\nSơ cấp là Hsk 1 và Hsk 2 (2 cấp độ này chưa được cấp chứng chỉ vì dễ)\r\nTrung cấp là HSK 3 và HSK 4\r\nCao cấp là HSK 5 và HSK 6\r\nSắp tới sẽ có xuất hiện HSK789.', NULL, NULL);
INSERT INTO `tips` VALUES (37304, 'HSK 2 là mức cơ bản trong hệ thông cấp bậc HSK. Ở cấp thi này bạn cần có kỹ năng\r\nNghe hiểu và đọc hiểu rất đơn giản. Thời gian làm bài HSK 2 là 55 phút, trong đó có 35 câu nghe trong vòng\r\n25 phút. Phần đọc hiểu 20 phút với 25 câu hỏi. 5 phút dùng để điền thông tin cơ bản và 5 phút còn lại để\r\nđiền đáp áp vào giấy trả lời.', NULL, NULL);
INSERT INTO `tips` VALUES (37305, 'Khi vận mẫu \'i\' đi với 7 thanh mẫu \'z\',\'c\',\'r\',\'s\',\'zh\',\'ch\',\'sh\' âm đọc sẽ biến\r\nthành \'ư\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37306, 'Mấy nét nghiêng của phần trên chữ CỐC 谷 chỉ nước chảy, khẩu ở dưới chỉ cửa núi.\r\nNghĩa gốc là dải đất hẹp và dài hoặc đường nước chảy giữa hai quả núi.', NULL, NULL);
INSERT INTO `tips` VALUES (37307, 'Trứng bách thảo là một món tuyệt vời và hầu như đều xuất hiện trong mọi bữa cơm\r\ncủa người Trung Hoa, trứng được ủ trong thời khá dài nên có hình hài rất lạ mắt bề ngoài trứng có màu cam\r\nhoặc nâu đâm còn bên trong lòng đỏ thì có màu xanh lục. Nhưng nhờ hương vị mằn mặn rất khác biệt mang lại\r\ncho cảm giác rất tuyệt vời.', NULL, NULL);
INSERT INTO `tips` VALUES (37308, '青 春 像 一 场 大 雨，无 论 感 冒 了 还 想 再 淋 一次。\r\nQīngchūn xiàng yī chǎng dàyǔ, wúlùn gǎnmàole hái xiǎng zài lín yīcì.\r\nThanh xuân giống như một cơn mưa rào, cho dù bị cảm lạnh bạn vẫn muốn đắm chìm trong cơn mưa ấy một lần nữa.', NULL, NULL);
INSERT INTO `tips` VALUES (37309, '结果 nếu đọc là \'jiéguǒ\' mang nghĩa kết quả. Khi đọc \'jiēguǒ\' mang nghĩa\r\nra trái.', NULL, NULL);
INSERT INTO `tips` VALUES (37310, '酋 nghĩa gốc là \'quan trông coi kho rượu\'. Chữ do bát (八) và dậu (酉 ) hợp thành.\r\nBát là phân chia, dậu là rượu. Thời xưa phàm nắm các công việc liên quan đến rượu, như chia rượu cho mọi\r\nngười uống được gọi là \'đại tủ\'.', NULL, NULL);
INSERT INTO `tips` VALUES (37311, 'Đạo giáo xuất sinh vào thời kỳ Đông Hán, Trung Quốc. Nó có lịch sử gần 2000 năm.\r\nĐạo giáo tôn người sáng lâp, Lão Tử là thủy tổ, hay cũng gọi là “Thái Thượng lão quân”.', NULL, NULL);
INSERT INTO `tips` VALUES (37312, 'Trung Quốc là quê hương của đồ gốm sứ, được xưng là “Đất nước đồ sứ”. Đồng thời\r\nkỹ thuật chế tác đồ sứ của Trung Hoa truyền rộng ra các nước, cũng góp phần truyền rộng văn hóa Trung Hoa ra\r\nthế giới.', NULL, NULL);
INSERT INTO `tips` VALUES (37313, 'Đúng với câu \'Cơm tàu vợ nhật\'\r\nẨm thực của Trung Hoa rất phong phú và đa dạng. Đồng thời ứng với mỗi ngày lễ, người Trung Hoa sẽ ăn một\r\nloại ẩm thực như bánh, món ăn … khác nhau. Vào tết âm lịch, người Trung Hoa ăn bánh chẻo (sủi cảo). Tết\r\nNguyên tiêu người Trung Hoa ăn bánh nguyên tiêu, một loại bánh giống bánh trôi nhưng cách làm khác. Vào ngày\r\ntết Đoan ngọ người Trung Hoa ăn bánh chưng… Mỗi một loại lễ hội, một loại ẩm thực để thể hiện một nội hàm ý\r\nnghĩa khác nhau.', NULL, NULL);
INSERT INTO `tips` VALUES (37314, 'Gà nướng đất sét là một món ăn mang tên gọi độc đáo, có nguồn gốc từ một câu\r\nchuyện dân gian truyền thống, và đến ngày nay vẫn được truyền bá. Gà được ướp gia vị kỹ càng, nhồi bên trong\r\nvới gừng thơm và lá chanh tươi, sau đó được bọc kín bằng giấy bạc và lớp đất sét. Khi gà được nướng chín,\r\ngia vị thấm sâu vào từng sợi thịt, tạo nên hương vị thơm ngon mê đắm khó cưỡng.', NULL, NULL);
INSERT INTO `tips` VALUES (37315, 'Câu chào hỏi thông thường trong tiếng Trung\r\nKhi muốn thực hiện lời chào ai đó trong tiếng Trung sẽ thực hiện theo cách:\r\nCấu trúc: Danh từ chỉ người + 好\r\nVí dụ:\r\n你好。 (Nǐ hǎo.)\r\nXin chào.\r\n老师好。 (Lǎoshī hǎo.)\r\nChào thầy/Chào cô', NULL, NULL);
INSERT INTO `tips` VALUES (37316, '\'贵人多忘事\' ý chỉ cách nói lịch sự để diễn tả rằng người ta hoàn toàn\r\ncó thể thông cảm cho ai đó khi họ quá bận rộn và quên một số việc nhỏ. Thỉnh thoảng câu này\r\ncòn dùng với hàm ý châm biếm.', NULL, NULL);
INSERT INTO `tips` VALUES (37317, 'Đồng hồ hoặc đồng hồ đeo tay (送钟, sòngzhōng) là điều cấm kỵ. Vì cụm từ này có\r\ncách phát âm giống 送终 (sòngzhōng) – để chăm sóc người già sắp chết hoặc dự đám tang. Tặng đồng hồ cho ai đó\r\nngụ ý rằng bạn đang chờ họ chết, thời gian chỉ còn ít ỏi.', NULL, NULL);
INSERT INTO `tips` VALUES (37318, 'Cụm từ \'团团圆圆\' mang nghĩa đoàn viên, sum vầy. Vì vậy ngày Tết người Trung thường\r\năn \'汤圆(bánh trôi)\' để mong một năm mới trọn vẹn xum vầy.', NULL, NULL);
INSERT INTO `tips` VALUES (37319, 'Trứng luộc nước tiểu là hững quả trứng sẽ được luộc bằng nước tiểu bé trai dưới\r\n10 tuổi qua một ngày và đem bày bán. Người dân nơi đây thường xuyên ăn chúng và khen ngon cùng quan niệm món\r\nnày giúp khỏe mạnh, tăng sự tập trung.', NULL, NULL);
INSERT INTO `tips` VALUES (37320, 'Các bạn rất hay quên phải bật hơi các âm p,t,k,c,ch,q. Hoặc nhiều khi đọc bật\r\nhơi nhầm sang các từ còn lại. Để luyện được âm bật hơi, bạn có thể chuẩn bị cho mình một mẩu giấy, khi phát\r\nâm bật hơi mẩu giấy rung lên thì mới được.', NULL, NULL);
INSERT INTO `tips` VALUES (37321, '\'吐槽\' ngôn ngữ mạng \'bóc phốt nhẹ\'', NULL, NULL);
INSERT INTO `tips` VALUES (37322, 'Câu đố:\r\n样子长得痩又长，\r\nYàngzi zhǎng de shòu yòu cháng,\r\n木头衣衫黑心肠，\r\nMùtou yīshān hēi xīncháng,\r\n嘴巴尖尖会说话。\r\nZuǐba jiānjiān huì shuōhuà.\r\n越说越矮变了样。\r\nYuè shuō yuè ǎi biàn le yàng.\r\nLà cái gì nhỉ?', NULL, NULL);
INSERT INTO `tips` VALUES (37323, '\'碾压\' ngôn ngữ mạng \'bị chèn,bị đè ép\'', NULL, NULL);
INSERT INTO `tips` VALUES (37324, 'HSKK cao cấp (HSKKCC) tương ứng cấp độ C của Khung Tham chiếu Ngôn ngữ Chung\r\nChâu Âu (CEF). Thí sinh vượt qua kỳ thi HSKK trung cấp có thể hiểu và sử dụng tiếng Trung để giao tiếp với\r\nngười bản ngữ tương đối thành thạo.', NULL, NULL);
INSERT INTO `tips` VALUES (37325, 'Giả tá vốn là không có chữ, mượn thanh để gửi sự, biến thành âm và nghĩa xa\r\nlạ, không có quan hệ duyên do suy diễn.\r\nHành trình tự học cần có lịch trình, kế hoạch cụ thể.', NULL, NULL);
INSERT INTO `tips` VALUES (37326, '\'下头\' ngôn ngữ mạng \'tụt hết cả cảm xúc\'', NULL, NULL);
